import {
  CREATE,
  FETCH_ITEM,
  FETCH_ALL,
  START_LOADING,
  END_LOADING,
  FETCH_BY_ITEM,
  FETCH_BY_SEARCH,
  FETCH_ON_SALE,
} from "../constants/actionTypes";

const initState = {
  items: [],
  item: [],
  question: "",
  answer: [],
  allAnswers: [],
  itemsOnSale: [],
  favorites: [],
  limit: 20,
  saleLimit: 20,
  searchLimit: 20,
  isLoading: true,
};

const itemsReducer = (state = initState, action) => {
  switch (action.type) {
    case FETCH_ALL:
    case FETCH_BY_ITEM:
    case FETCH_BY_SEARCH:
      return { ...state, items: action.payload };
    case START_LOADING:
      return { ...state, isLoading: true };
    case END_LOADING:
      return { ...state, isLoading: false };
    case CREATE:
      return { ...state, items: [...state.items, action.payload] };
    case FETCH_ITEM:
      return { ...state, item: action.payload.item };
    case FETCH_ON_SALE:
      return { ...state, itemsOnSale: action.payload };
    case "FETCH_FAVORITE":
      return {
        ...state,
        favorites: [...state.favorites, action.payload.favorite],
      };
    case "ASK":
      return { ...state, question: action.payload.question };
    case "FETCH_ANSWER":
      return { ...state, answer: action.payload.answer };
    case "FETCH_ALL_ANSWERS":
      return { ...state, allAnswers: action.payload.answer };
    case "FETCH_SALE":
      return { ...state, sale: action.payload.sale };
    case "CHANGE_LIMIT":
      return { ...state, limit: state.limit + action.payload.number };
    case "CHANGE_SALE_LIMIT":
      return { ...state, saleLimit: state.saleLimit + action.payload.number };
    case "CHANGE_SEARCH_LIMIT":
      return {
        ...state,
        searchLimit: state.searchLimit + action.payload.number,
      };
    default:
      return { ...state };
  }
};

export default itemsReducer;
