import React, { useEffect, useState } from "react";
//styling
import styled from "styled-components";
//components
import Button from "../components/Button";
//material ui
import Breadcrumbs from "@material-ui/core/Breadcrumbs";
//react router
import { Link, useHistory } from "react-router-dom";
//components
import Order from "../components/OrderDetails";
import OrderCard from "../components/OrderCard";
//redux
import { useDispatch, useSelector } from "react-redux";
//actions
import { getUserOrders } from "../actions/ordersAction";

const OrderDetails = ({ user, orderDetails }) => {
  const [order, setOrder] = useState([]);
  const dispatch = useDispatch();
  const history = useHistory();
  useEffect(() => {
    if (user) {
      dispatch(getUserOrders(user._id));
    }
  }, [dispatch, user]);
  const { userOrders } = useSelector((state) => state.orders);
  const orderDetailsHandler = (id, order) => {
    history.push(`/customer/account/orders/${id}`);
    setOrder(order);
    window.scrollTo(0, 0);
  };
  return (
    <OrdersComponent>
      {userOrders.length > 0 ? (
        <>
          <Breadcrumbs aria-label="breadcrumb">
            <span>My account</span>
            <Link to="/customer/account/orders" className="link">
              My orders
            </Link>
            {orderDetails && <span>{orderDetails}</span>}
          </Breadcrumbs>
          {userOrders && !orderDetails ? (
            <>
              {userOrders.map((order) => (
                <OrderCard
                  key={order._id}
                  order={order}
                  orderDetailsHandler={orderDetailsHandler}
                />
              ))}
            </>
          ) : (
            <div className="specific-order">
              <Order />
            </div>
          )}
        </>
      ) : (
        <div className="no-orders">
          <h2>You didnt order anything</h2>
          <span>Go and explore</span>
          <div className="buttons">
            <Link to="/woman">
              <Button label="Women" />
            </Link>
            <Link to="/man">
              <Button label="Men" />
            </Link>
          </div>
        </div>
      )}
    </OrdersComponent>
  );
};

const OrdersComponent = styled.div`
  margin-left: 2rem;
  @media screen and (max-width: 1000px) {
    margin-left: 0;
  }
`;

export default OrderDetails;
