    align-items: center;
    @media screen and (max-width: 1000px) {
      flex-direction: column;
      align-items: flex-start;
      font-size: 1rem;
    }
  }
  .option {
    font-size: 2rem;
    margin: 1rem 0rem;
    font-weight: bold;
    @media screen and (max-width: 1000px) {
      font-size: 1rem;
    }
