      }
    }
    .images-display {
      display: grid;
      grid-template-columns: minmax(auto, auto) 1fr 1fr;
    }
    .imageWrapper {
      display: flex;
      justify-content: center;
      flex-direction: column;
      font-size: 1rem;
      font-weight: normal;
      margin: 1rem 0rem;
      padding: 1rem;
      border: 1px solid black;
      .image {
        height: 200px;
        width: 200px;
      }
      .image-remove {
        padding: 0rem 1rem;
        &:hover {
          cursor: pointer;
          font-weight: bold;
        }
      }
    }
  }
`;

export default AddItemPage;
