import React, { useState } from "react";
//actions
import { deleteAddress } from "../actions/userActions";
//redux
import { useDispatch } from "react-redux";
//material ui
import Checkbox from "@material-ui/core/Checkbox";

const Address = ({ address, chooseAddressHandler, setAddressId, userId }) => {
  const dispatch = useDispatch();
  const [checkbox, setCheckbox] = useState(false);
  const checkboxHandler = () => {
    setCheckbox(true);
    setAddressId(address._id);
    chooseAddressHandler(address);
  };
  return (
    <div
      className="single-address"
      onClick={() =>
        chooseAddressHandler(
          address.city,
          address.houseNr,
          address.street,
          address.postalCode,
          address.phone,
          address._id
        )
      }
    >
      <Checkbox checked={checkbox} onChange={checkboxHandler} color="primary" />
      <div className="address-info">
        <span>
          {address.name} {address.surname}
        </span>
        <span>
          {address.street} {address.houseNr}
        </span>
        <span>
          {address.postalCode} {address.city}
        </span>
        <span> Tel. {address.phone}</span>
        <p onClick={() => dispatch(deleteAddress(userId, address._id))}>
          Delete address
        </p>
      </div>
    </div>
  );
};

export default Address;
