import React, { useState, useEffect } from "react";
//styling
import styled from "styled-components";
//data
import { addressInputs } from "../descriptions/inputs";
//components
import Input from "../components/Input";
import Button from "../components/Button";
import Address from "../components/Address";
//actions
import { updateAddress, addAddress } from "../actions/userActions";
//redux
import { useDispatch } from "react-redux";

const AddressComponent = ({ user }) => {
  const [addressData, setAddressData] = useState({
    street: "",
    city: "",
    postalCode: "",
    houseNr: "",
    phone: "",
  });
  const [addressId, setAddressId] = useState(0);
  const [action, setAction] = useState("change");
  const dispatch = useDispatch();
  useEffect(() => {
    setAddressId(user?.address[0]?._id);
  }, [user]);
  //handlers
  const clearAddress = () =>
    setAddressData({
      street: "",
      city: "",
      postalCode: "",
      houseNr: "",
      phone: "",
    });
  const chooseAddressHandler = ({
    city,
    houseNr,
    street,
    postalCode,
    phone,
    _id,
  }) => {
    setAddressData({
      street: street,
      city: city,
      postalCode: postalCode,
      houseNr: houseNr,
      phone: phone,
    });
    setAction("change");
    setAddressId(_id);
  };
  //add new address
  const newAddressHandler = () => {
    setAction("add");
    clearAddress();
    setAddressId("");
  };
  const addressHandler = () => {
    if (action === "change") {
      dispatch(updateAddress(user._id, { address: addressData }));
    } else {
      dispatch(addAddress(user._id, { address: addressData }));
    }
    clearAddress();
  };
  useEffect(() => {
    if (user?.address?.length === 0) {
      newAddressHandler();
    }
  }, [user]);
  useEffect(() => {
    user.address.map((add) =>
      add.id === addressId ? chooseAddressHandler(add) : add
    );
  }, [user]);
  const handleForm = (e) =>
    setAddressData({ ...addressData, [e.target.name]: e.target.value });
  return (
    <AddressWrapper>
      <h2>My account</h2>
      <div className="line"></div>
      <h2>Addresses</h2>
      <div className="choose-address">
        {user?.address?.map((address) => (
          <Address
            key={address._id}
            chooseAddressHandler={chooseAddressHandler}
            address={address}
            userId={user._id}
            setAddressId={setAddressId}
          />
        ))}
      </div>
      <div className="line"></div>
      <Button onClick={() => newAddressHandler()} label="Add new address" />
      <div className="inputs-grid">
        {addressInputs.map((input) => (
          <div key={input.label} className="grid-item">
            <Input
              label={input.label}
              name={input.name}
              value={addressData[input.name]}
              type={input.type}
              handleChange={(e) => handleForm(e)}
              required={input.required}
            />
          </div>
        ))}
      </div>
      <Button
        onClick={() => addressHandler()}
        label={action === "change" ? "Save changes" : "Add"}
        variant="black"
      />
    </AddressWrapper>
  );
};

const AddressWrapper = styled.div`
  display: flex;
  flex-direction: column;
  margin-left: 2rem;
  @media screen and (max-width: 1000px) {
    margin: 0;
  }
  .choose-address {
    display: flex;
    align-items: start;
    .active-address {
      .checkbox {
        background-color: black;
      }
    }
    .single-address {
      display: flex;
      font-size: 0.8rem;
      &:hover {
        cursor: pointer;
      }
      .checkbox {
        border: 1px solid rgba(0, 0, 0, 0.2);
        width: 1rem;
        height: 1rem;
        margin: 0 1rem;
        @media screen and (max-width: 1000px) {
          width: 1rem;
          height: 1rem;
          border-radius: 1rem;
          margin: 0 0.5rem;
        }
      }
      .address-info {
        display: flex;
        flex-direction: column;
        p {
          margin: 5px 0;
          font-weight: bold;
          &:hover {
            cursor: pointer;
          }
        }
      }
    }
  }
  .button-white {
    @media screen and (max-width: 1000px) {
      display: flex;
      text-transform: upperCase;
      justify-content: center;
      width: 100%;
      background-color: black;
      margin: 1rem 0;
      padding: 0;
      font-size: 1.5rem;
      color: White;
    }
  }
`;

export default AddressComponent;
