import React, { useEffect, useState } from "react";
//styling
import styled from "styled-components";
//router
import { useLocation } from "react-router";
import { Link } from "react-router-dom";
//actions
import { getOrders } from "../actions/ordersAction";
//redux
import { useDispatch, useSelector } from "react-redux";

import Breadcrumbs from "@material-ui/core/Breadcrumbs";
//components
import UserDetails from "../components/UserDetails";
import ItemsComponent from "../components/ItemsComponent";
import OrdersComponent from "./OrdersComponent";
import UsersComponent from "./UsersComponent";
//notistack
import { useSnackbar } from "notistack";

const AdminPanel = () => {
  const dispatch = useDispatch();
  //state
  const { enqueueSnackbar, closeSnackbar } = useSnackbar();
  const { user } = useSelector((state) => state.user);
  const { orders } = useSelector((state) => state.orders);
  const location = useLocation();
  const pathName = location.pathname.split("/")[3];
  const details = location.pathname.split("/")[4];
  //orders state
  const [ordersDate, setOrdersDate] = useState("");
  const [ordersPage, setOrdersPage] = useState(1);
  const [ordersPageIndex, setOrdersPageIndex] = useState(1);
  //users state
  const [userId, setUserId] = useState("");

  const [pageIndex, setPageIndex] = useState(1);
  //useEffect
  useEffect(() => {
    if (user) {
      dispatch(getOrders());
    }
  }, [dispatch]);

  //handlers
  const snackbarHandler = (snackbarMessage, snackVariant) => {
    enqueueSnackbar(snackbarMessage, { variant: snackVariant });
    closeSnackbar(500);
  };
  const ordersSortHandler = (e) => {
    setOrdersDate(e.target.value);
  };
  const handleOrdersPage = (e, v) => {
    setOrdersPage(v);
    window.scrollTo(0, 0);
  };

  return (
    <AdminPanelComponent>
      <div className="left-side">
        <ul>
          <Link to="/admin/panel/orders" className="link">
            <li>Manage Orders</li>
          </Link>
          <Link to="/admin/panel/items" className="link">
            <li>Manage Items</li>
          </Link>
          {user.role === "headAdmin" && (
            <Link to="/admin/panel/users" className="link">
              <li>Manage users</li>
            </Link>
          )}
          <Link to="/add-item" className="link">
            <li>Add Item</li>
          </Link>
        </ul>
      </div>
      <div className="right-side">
        {pathName === "orders" && (
          <OrdersComponent
            orderDetails={details}
            title="Admin panel"
            link={{ label: "orders", path: "/admin/panel/orders" }}
            orders={orders}
            snackbarHandler={snackbarHandler}
          />
        )}
        {pathName === "users" && !details && user.role === "headAdmin" && (
          <UsersComponent setUserId={setUserId} />
        )}
        {details && pathName === "users" && (
          <>
            <Breadcrumbs aria-label="breadcrumb">
              <span>Admin panel</span>
              <Link to="/admin/panel/users" className="link">
                users
              </Link>
              <span>{userId}</span>
            </Breadcrumbs>
            <UserDetails />
          </>
        )}
        {pathName === "items" && <ItemsComponent />}
      </div>
    </AdminPanelComponent>
  );
};

const AdminPanelComponent = styled.div`
  display: flex;
  min-height: 50rem;
  margin-top: 2rem;
  @media screen and (max-width: 1000px) {
    flex-direction: column;
  }
  .left-side {
    width: 20%;
    display: flex;
    flex-direction: Column;
    align-items: center;
    @media screen and (max-width: 1000px) {
      width: 100%;
    }
    ul {
      list-style: none;
      li {
        padding: 1rem 0;
      }
    }
  }
  .right-side {
    width: 70%;
    margin-left: 2rem;
    @media screen and (max-width: 1000px) {
      margin-left: 0;
      width: 100%;
    }
    .orders-component {
      width: 100%;
      display: flex;
      flex-direction: column;
      .sort-select {
        width: 10rem;
      }
      .orders {
        margin-left: 2rem;
        @media screen and (max-width: 1000px) {
          margin-left: 0;
        }
        .order {
          display: flex;
          justify-content: space-between;
          font-size: 1.5rem;
          border-bottom: 1px solid rgba(0, 0, 0, 0.2);
          padding: 1rem;

          .order-left {
            display: flex;
            flex-direction: column;
            justify-content: space-between;
            .price-info {
              display: flex;
              flex-direction: column;
              font-size: 1rem;
            }
            .items-image {
              display: flex;
              flex-wrap: wrap;
              img {
                margin: 0.5rem 0.5rem 0.5rem 0;
                height: 10rem;
                width: 8rem;
              }
            }
          }
          .order-right {
            display: flex;
            flex-direction: column;
            justify-content: space-between;
            .time-info {
              display: flex;
              flex-direction: column;
              font-size: 1rem;
              span {
                align-self: flex-end;
              }
            }
            .details {
              align-self: flex-end;
              span {
                padding: 0.6rem;
                font-size: 1rem;
                font-weight: bold;
                text-transform: upperCase;
                &:hover {
                  background-color: rgba(0, 0, 0, 0.2);
                  cursor: pointer;
                }
              }
            }
          }
        }
      }
      .pagination {
        display: flex;
        justify-content: Center;
        align-items: Center;
        margin: 1rem;
      }
    }
    .sort-select {
      width: 10rem;
      @media screen and (max-width: 1000px) {
        width: 7rem;
        font-size: 1rem;
      }
    }
    .users-component {
      .sort-users {
        display: flex;
        padding: 0 10px;
        .search-users {
          margin: 0 1rem;
        }
      }
      .users-table {
        margin-top: 1rem;
        width: 100%;
        td,
        th {
          border: 1px solid rgba(0, 0, 0, 0.4);
          text-align: left;
          padding: 8px;
        }
        th {
          font-weight: bold;
        }
        @media screen and (max-width: 1000px) {
          font-size: 0.9rem;
        }
      }
      .pagination {
        display: flex;
        justify-content: Center;
        align-items: center;
        margin: 1rem 0;
      }
    }
  }
`;

export default AdminPanel;
