import React, { useState, useEffect } from "react";
//react router
import { useLocation } from "react-router";
import { Link, useHistory } from "react-router-dom";
//redux
import { useDispatch, useSelector } from "react-redux";
//actions
import { updateUser } from "../actions/userActions";
import { loadUsersOrders } from "../actions/ordersAction";
//styling
import styled from "styled-components";
//material ui
import Checkbox from "@material-ui/core/Checkbox";
import Breadcrumbs from "@material-ui/core/Breadcrumbs";
//icons
import ShoppingBasketIcon from "@material-ui/icons/ShoppingBasket";
import AccountBoxIcon from "@material-ui/icons/AccountBox";
import MeetingRoomIcon from "@material-ui/icons/MeetingRoom";
import HomeIcon from "@material-ui/icons/Home";
//data
import {
  accountInputs,
  passwordInputs,
  addressInputs,
} from "../descriptions/inputs";
//components
import Order from "../components/Order";
import Input from "../components/Input";
import Button from "../components/Button";

const AccountPage = () => {
  const [deleteInfo, setDeleteInfo] = useState(false);
  //users State
  const [userData, setUserData] = useState({
    name: "",
    surname: "",
    email: "",
    phone: "",
  });
  const [addressData, setAddressData] = useState({
    street: "",
    city: "",
    postalCode: "",
    houseNr: "",
  });
  const [passwordData, setPasswordData] = useState({
    password: "",
    newPassword: "",
    confirmPassword: "",
  });
  const [addressMsg, setAddressMsg] = useState("");
  const [newAddressCheckbox, setNewAddressCheckbox] = useState(false);
  const [addressId, setAddressId] = useState(1);
  const [order, setOrder] = useState([]);
  //account state
  const [passwordErrorMsg, setPasswordErrorMsg] = useState("");
  const [accountErrorMsg, setAccountErrorMsg] = useState("");
  //use
  const location = useLocation();
  const history = useHistory();
  const dispatch = useDispatch();
  //pathName
  const pathName = location.pathname.split("/")[3];
  const orderDetails = location.pathname.split("/")[4];
  const [action, setAction] = useState("change");
  const { user } = useSelector((state) => state.user);
  console.log(user);
  useEffect(() => {
    if (user) {
      dispatch(loadUsersOrders(user.id));
    }
  }, [dispatch, user]);
  const { userOrders } = useSelector((state) => state.orders);

  //handlers
  const userAccountHandler = () => {};

  const passwordChangeHandler = () => {};
  const deleteAccountHandler = () => {};
  //edits address
  const addressHandler = () => {};
  const clearAddress = () =>
    setAddressData({
      street: "",
      city: "",
      postalCode: "",
      houseNr: "",
    });
  //adds address
  const newAddressHandler = () => {
    setNewAddressCheckbox(true);
    setAddressId(0);
    setAction("add");
    clearAddress();
  };
  useEffect(() => {
    if (user?.address?.length === 0) {
      newAddressHandler();
    }
  }, [user]);
  const addAddress = () => {
    dispatch(updateUser(user._id, { address: addressData }));
    clearAddress();
  };
  const checkboxHandler = (e) => {
    setNewAddressCheckbox(e.target.checked);
    if (newAddressCheckbox === false) {
      newAddressHandler();
    }
  };
  const LogOutHandler = () => {
    localStorage.removeItem("userId");
    history.push("/customer/account/login");
  };
  //deletes address
  const deleteAddressHandler = (id) => {};
  const chooseAddressHandler = (city, houseNr, street, postalCode, id) => {
    setAddressData({
      street: street,
      city: city,
      postalCode: postalCode,
      houseNr: houseNr,
    });
    setAction("change");
    setAddressId(id);
    setNewAddressCheckbox(false);
  };
  const orderDetailsHandler = (id, order) => {
    history.push(`/customer/account/orders/${id}`);
    setOrder(order);
    window.scrollTo(0, 0);
  };
  const handleForm = (e, setData, data) =>
    setData({ ...data, [e.target.name]: e.target.value });
  return (
    <>
      {user && (
        <LoggedInComponent>
          <div className="left-side">
            <ul>
              <Link to="/customer/account/orders" className="link">
                <li className={pathName === "orders" ? "active" : ""}>
                  <ShoppingBasketIcon /> Orders
                </li>
              </Link>
              <Link to="/customer/account/address" className="link">
                <li className={pathName === "address" ? "active" : ""}>
                  <HomeIcon /> Address data
                </li>
              </Link>

              <Link to="/customer/account/info" className="link">
                <li className={pathName === "info" ? "active" : ""}>
                  <AccountBoxIcon /> Account info
                </li>
              </Link>
              <li className="log-out" onClick={() => LogOutHandler()}>
                <MeetingRoomIcon /> Log out
              </li>
            </ul>
          </div>
          {user && (
            <div className="right-side">
              {pathName === "info" && (
                <div className="infoComponent">
                  <div className="info">
                    <span>My account</span>
                    <div className="line"></div>
                    <span>{accountErrorMsg}</span>
                    {accountInputs.map((input) => (
                      <span>
                        <Input
                          label={input.label}
                          name={input.name}
                          value={userData[input.name]}
                          type={input.type}
                          handleChange={(e) =>
                            handleForm(e, setUserData, userData)
                          }
                          required={input.required}
                        />
                      </span>
                    ))}
                    <Button
                      onClick={() => userAccountHandler()}
                      label="Save changes"
                      variant="black"
                    />
                    <div className="line"></div>
                    <div className="password-change">
                      <span>Password change</span>
                      <span>{passwordErrorMsg}</span>
                      {passwordInputs.map((input) => (
                        <Input
                          label={input.label}
                          name={input.name}
                          value={passwordData[input.name]}
                          type={input.type}
                          handleChange={(e) =>
                            handleForm(e, setPasswordData, passwordData)
                          }
                          required={input.required}
                        />
                      ))}
                      <Button
                        onClick={() => passwordChangeHandler()}
                        label="Change password"
                        variant="black"
                      />
                    </div>
                    <div className="line"></div>
                    <div className="delete-acc">
                      <span onClick={() => setDeleteInfo(true)}>
                        Delete account
                      </span>
                      <div
                        style={{ display: deleteInfo ? "block" : "none" }}
                        className="delete-confirmation"
                      >
                        <span>Are you sure you want to delete account?</span>
                        <div className="buttons">
                          <Button
                            onClick={() => deleteAccountHandler()}
                            label="Yes"
                          />
                          <Button
                            onClick={() => setDeleteInfo(false)}
                            label="No"
                            variant="black"
                          />
                        </div>
                      </div>
                    </div>
                    <div className="line"></div>
                  </div>
                </div>
              )}
              {pathName === "orders" && !orderDetails && (
                <div className="orders">
                  {userOrders.length > 0 ? (
                    <>
                      <Breadcrumbs aria-label="breadcrumb">
                        <span>My account</span>
                        <Link to="/customer/account/orders" className="link">
                          My orders
                        </Link>
                      </Breadcrumbs>
                      {userOrders.map((order) => (
                        <div className="order" key={order.id}>
                          <div className="order-left">
                            <div className="price-info">
                              <span>Nr:{order.id}</span>
                              <span>
                                <b>
                                  {(
                                    order.cartPrice + order.deliveryPrice
                                  ).toFixed(2)}{" "}
                                  GBP
                                </b>
                              </span>
                            </div>
                            <div className="items-image">
                              {order.items.map((item) => (
                                <img
                                  src={item.images[0].img}
                                  alt={item.name}
                                  key={item.id}
                                  onClick={() =>
                                    orderDetailsHandler(order.id, order)
                                  }
                                />
                              ))}
                            </div>
                          </div>
                          <div className="order-right">
                            <div className="time-info">
                              <span>
                                {order.date} {order.time}
                              </span>
                              <span>{order.status}</span>
                            </div>
                            <div className="details">
                              <span
                                className="details-button"
                                onClick={() =>
                                  orderDetailsHandler(order.id, order)
                                }
                              >
                                Details
                              </span>
                            </div>
                          </div>
                        </div>
                      ))}
                    </>
                  ) : (
                    <div className="no-orders">
                      <h2>You didnt order anything</h2>
                      <span>Go and explore</span>
                      <div className="buttons">
                        <Link to="/woman">
                          <Button label="Women" />
                        </Link>
                        <Link to="/man">
                          <Button label="Men" />
                        </Link>
                      </div>
                    </div>
                  )}
                </div>
              )}
              {orderDetails && (
                <div className="specific-order">
                  <Breadcrumbs aria-label="breadcrumb">
                    <span>My account</span>
                    <Link to="/customer/account/orders" className="link">
                      My orders
                    </Link>
                    <span>{order.id}</span>
                  </Breadcrumbs>
                  <Order />
                </div>
              )}
              {pathName === "address" && (
                <div className="addressComponent">
                  <h2>My account</h2>
                  <div className="line"></div>
                  <h2>Addresses</h2>
                  <div className="choose-address">
                    {user?.address.map((info, index) => (
                      <div
                        key={info.id}
                        className={
                          index + 1 === addressId
                            ? "single-address active-address"
                            : "single-address"
                        }
                        onClick={() =>
                          chooseAddressHandler(
                            info.name,
                            info.surname,
                            info.city,
                            info.phone,
                            info.houseNr,
                            info.street,
                            info.postalCode,
                            info.id
                          )
                        }
                      >
                        <div className="checkbox"></div>
                        <div className="address-info">
                          <span>
                            {info.name} {info.surname}
                          </span>
                          <span>
                            {info.street} {info.houseNr}
                          </span>
                          <span>
                            {info.postalCode} {info.city}
                          </span>
                          <span> Tel. {info.phone}</span>
                          <p onClick={() => deleteAddressHandler(info.id)}>
                            Delete address
                          </p>
                        </div>
                      </div>
                    ))}
                    <div className="address">
                      <Checkbox
                        checked={newAddressCheckbox}
                        onChange={checkboxHandler}
                        color="primary"
                      />
                      <span
                        style={{ fontSize: "0.8rem", padding: "1rem 0" }}
                        onClick={() => newAddressHandler()}
                      >
                        Add new address
                      </span>
                    </div>
                  </div>
                  <span>{addressMsg}</span>
                  <div className="line"></div>
                  <div className="inputs-grid">
                    {addressInputs.map((input) => (
                      <div key={input.label} className="grid-item">
                        <Input
                          label={input.label}
                          name={input.name}
                          value={addressData[input.name]}
                          type={input.type}
                          handleChange={(e) =>
                            handleForm(e, setAddressData, addressData)
                          }
                          required={input.required}
                        />
                      </div>
                    ))}
                  </div>
                  <Button
                    onClick={() => addAddress()}
                    label="Add"
                    style={{ display: action === "add" ? "flex" : "none" }}
                  />
                  <Button
                    onClick={() => addressHandler()}
                    label="Save changes"
                    variant="black"
                    style={{ display: action === "change" ? "flex" : "none" }}
                  />
                </div>
              )}
            </div>
          )}
        </LoggedInComponent>
      )}
    </>
  );
};

