import React from "react";
//styling
import styled from "styled-components";

const Button = ({ label, variant, size }) => {
  return (
    <ButtonWrapper variant={variant} size={size}>
      {label}
    </ButtonWrapper>
  );
};

const ButtonWrapper = styled.div`
  height: ${({ size }) => (size === "lg" ? "4rem" : "2rem")};
  width: ${({ size }) => (size === "lg" ? "20rem" : "10rem")};
  font-size: ${({ size }) => (size === "lg" ? "20px" : "10px")};
  display: flex;
  align-items: center;
  justify-content: center;
  text-transform: upperCase;
  padding: 0.5rem;
  cursor: pointer;
  margin: 10px 0;
  border: ${({ variant }) => (variant === "black" ? 0 : "1px solid black")};
  background-color: ${({ variant }) =>
    variant === "black" ? "black" : "white"};
  color: ${({ variant }) => (variant === "black" ? "white" : "black")};
  transition: 0.3s ease-in all;
  &:hover {
    background-color: ${({ variant }) =>
      variant === "black" ? "white" : "black"};
    color: ${({ variant }) => (variant === "black" ? "black" : "white")};
  }
`;

export default Button;
