import React, { useState, useEffect } from "react";
//styling
import styled from "styled-components";
//data
import { addressInputs } from "../descriptions/inputs";
//material ui
import Checkbox from "@material-ui/core/Checkbox";
//components
import Input from "../components/Input";
import Button from "../components/Button";
//actions
import { updateUserAddress } from "../actions/userActions";
//redux
import { useDispatch } from "react-redux";

const AddressComponent = ({ user }) => {
  const [addressData, setAddressData] = useState({
    street: "",
    city: "",
    postalCode: "",
    houseNr: "",
    phone: "",
  });
  const [checkbox, setCheckbox] = useState(false);
  const [addressId, setAddressId] = useState(1);
  const [action, setAction] = useState("change");
  const dispatch = useDispatch();
  //handlers
  const clearAddress = () =>
    setAddressData({
      street: "",
      city: "",
      postalCode: "",
      houseNr: "",
    });
  //deletes address
  const deleteAddressHandler = (id) => {};
  const chooseAddressHandler = (
    city,
    houseNr,
    street,
    postalCode,
    phone,
    id
  ) => {
    setAddressData({
      street: street,
      city: city,
      postalCode: postalCode,
      houseNr: houseNr,
      phone: phone,
    });
    setAction("change");
    setAddressId(id);
    setCheckbox(false);
  };
  //add new address
  const newAddressHandler = () => {
    setCheckbox(true);
    setAddressId(0);
    setAction("add");
    clearAddress();
  };
  const addressHandler = () => {
    dispatch(updateUserAddress(user._id, { address: addressData }));
    clearAddress();
  };
  useEffect(() => {
    if (user?.address?.length === 0) {
      newAddressHandler();
    }
  }, [user]);
  const checkboxHandler = (e) => {
    setCheckbox(e.target.checked);
    if (checkbox === false) {
      newAddressHandler();
    }
  };
  const handleForm = (e) =>
    setAddressData({ ...addressData, [e.target.name]: e.target.value });
  return (
    <AddressWrapper>
      <h2>My account</h2>
      <div className="line"></div>
      <h2>Addresses</h2>
      <div className="choose-address">
        {user?.address?.map((info, index) => (
          <div
            key={info.id}
            className={
              index + 1 === addressId
                ? "single-address active-address"
                : "single-address"
            }
            onClick={() =>
              chooseAddressHandler(
                info.city,
                info.houseNr,
                info.street,
                info.postalCode,
                info.phone,
                info.id
              )
            }
          >
            <div className="checkbox"></div>
            <div className="address-info">
              <span>
                {info.name} {info.surname}
              </span>
              <span>
                {info.street} {info.houseNr}
              </span>
              <span>
                {info.postalCode} {info.city}
              </span>
              <span> Tel. {info.phone}</span>
              <p onClick={() => deleteAddressHandler(info.id)}>
                Delete address
              </p>
            </div>
          </div>
        ))}
        <div className="address">
          <Checkbox
            checked={checkbox}
            onChange={checkboxHandler}
            color="primary"
          />
          <span
            style={{ fontSize: "0.8rem", padding: "1rem 0" }}
            onClick={() => newAddressHandler()}
          >
            Add new address
          </span>
        </div>
      </div>
      <div className="line"></div>
      <div className="inputs-grid">
        {addressInputs.map((input) => (
          <div key={input.label} className="grid-item">
            <Input
              label={input.label}
              name={input.name}
              value={addressData[input.name]}
              type={input.type}
              handleChange={(e) => handleForm(e)}
              required={input.required}
            />
          </div>
        ))}
      </div>
      <Button
        onClick={() => addressHandler()}
        label={action === "change" ? "Save changes" : "Add"}
        variant="black"
      />
    </AddressWrapper>
  );
};

const AddressWrapper = styled.div`
  display: flex;
  flex-direction: column;
  margin-left: 2rem;
  @media screen and (max-width: 1000px) {
    margin: 0;
  }
  .choose-address {
    display: flex;
    align-items: start;
    .active-address {
      .checkbox {
        background-color: black;
      }
    }
    .single-address {
      display: flex;
      font-size: 0.8rem;
      &:hover {
        cursor: pointer;
      }
      .checkbox {
        border: 1px solid rgba(0, 0, 0, 0.2);
        width: 1rem;
        height: 1rem;
        margin: 0 1rem;
        @media screen and (max-width: 1000px) {
          width: 1rem;
          height: 1rem;
          border-radius: 1rem;
          margin: 0 0.5rem;
        }
      }
      .address-info {
        display: flex;
        flex-direction: column;
        p {
          margin: 5px 0;
          font-weight: bold;
          &:hover {
            cursor: pointer;
          }
        }
      }
    }
  }
  .button-white {
    @media screen and (max-width: 1000px) {
      display: flex;
      text-transform: upperCase;
      justify-content: center;
      width: 100%;
      background-color: black;
      margin: 1rem 0;
      padding: 0;
      font-size: 1.5rem;
      color: White;
    }
  }
`;

export default AddressComponent;
