import React, { useEffect, useState } from "react";
//styling
import styled from "styled-components";
//router
import { useLocation } from "react-router";
import { Link } from "react-router-dom";
//actions
import { getOrders } from "../actions/ordersAction";
//redux
import { useDispatch, useSelector } from "react-redux";

import Breadcrumbs from "@material-ui/core/Breadcrumbs";
//components
import UserDetails from "../components/UserDetails";
import ItemsComponent from "../components/ItemsComponent";
import OrdersComponent from "./OrdersComponent";
import UsersComponent from "../components/UsersComponent";
//notistack
import { useSnackbar } from "notistack";

const AdminPanel = () => {
  const dispatch = useDispatch();
  //state
  const { enqueueSnackbar, closeSnackbar } = useSnackbar();
  const { user } = useSelector((state) => state.user);
  const { orders, numberOfPages } = useSelector((state) => state.orders);
  const location = useLocation();
  const pathName = location.pathname.split("/")[3];
  const details = location.pathname.split("/")[4];
  //users state
  const [userId, setUserId] = useState("");
  //useEffect
  useEffect(() => {
    dispatch(getOrders());
  }, [dispatch]);
  //handlers
  const snackbarHandler = (snackbarMessage, snackVariant) => {
    enqueueSnackbar(snackbarMessage, { variant: snackVariant });
    closeSnackbar(500);
  };

  return (
    <AdminPanelComponent>
      <div className="left-side">
        <ul>
          <Link to="/admin/panel/orders" className="link">
            <li>Manage Orders</li>
          </Link>
          <Link to="/admin/panel/items" className="link">
            <li>Manage Items</li>
          </Link>
          {user.role === "headAdmin" && (
            <Link to="/admin/panel/users" className="link">
              <li>Manage users</li>
            </Link>
          )}
          <Link to="/add-item" className="link">
            <li>Add Item</li>
          </Link>
        </ul>
      </div>
      <div className="right-side">
        {pathName === "orders" && (
          <OrdersComponent
            orderDetails={details}
            title="Admin panel"
            link={{ label: "orders", path: "/admin/panel/orders" }}
            orders={orders}
            snackbarHandler={snackbarHandler}
            numberOfPages={numberOfPages}
          />
        )}
        {pathName === "users" && !details && user.role === "headAdmin" && (
          <UsersComponent setUserId={setUserId} />
        )}
        {details && pathName === "users" && (
          <>
            <Breadcrumbs aria-label="breadcrumb">
              <span>Admin panel</span>
              <Link to="/admin/panel/users" className="link">
                users
              </Link>
              <span>{userId}</span>
            </Breadcrumbs>
            <UserDetails />
          </>
        )}
        {pathName === "items" && <ItemsComponent />}
      </div>
    </AdminPanelComponent>
  );
};

const AdminPanelComponent = styled.div`
  display: flex;
  min-height: 50rem;
  margin-top: 2rem;
  @media screen and (max-width: 1000px) {
    flex-direction: column;
  }
  .left-side {
    width: 20%;
    display: flex;
    flex-direction: Column;
    align-items: center;
    @media screen and (max-width: 1000px) {
      width: 100%;
    }
    ul {
      list-style: none;
      li {
        padding: 1rem 0;
        text-decoration: upperCase;
      }
    }
  }
  .right-side {
    width: 70%;
    margin-left: 2rem;
    @media screen and (max-width: 1000px) {
      margin-left: 0;
      width: 100%;
    }
  }
`;

export default AdminPanel;
