import React, { useState, useEffect, useLayoutEffect } from "react";
//styling
import styled from "styled-components";
//material ui
import Accordion from "@material-ui/core/Accordion";
import AccordionSummary from "@material-ui/core/AccordionSummary";
import AccordionDetails from "@material-ui/core/AccordionDetails";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
//router
import { Link } from "react-router-dom";
//redux
import { useDispatch, useSelector } from "react-redux";
//actions
import { getItem, deleteItem } from "../actions/itemsAction";
//router
import { useLocation, useHistory } from "react-router-dom";
//icons
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import ArrowForwardIosIcon from "@material-ui/icons/ArrowForwardIos";
import ArrowBackIosIcon from "@material-ui/icons/ArrowBackIos";
import LocalMallIcon from "@material-ui/icons/LocalMall";
import EditIcon from "@material-ui/icons/Edit";
import DeleteIcon from "@material-ui/icons/Delete";
//components
import SmallImage from "../components/SmallImage";
import CheckoutModal from "../components/CheckoutModal";
import FullImageModal from "../components/FullImageModal";
import SimilarItems from "../components/SimilarItems";
import ShowLoading from "../components/ShowLoading";
import Button from "../components/Button";

const ItemDetailsPage = () => {
  //state
  const [currentIndex, setCurrentIndex] = useState(0);
  const [itemsSize, setItemsSize] = useState("");
  const [proceedError, setProceedError] = useState(false);
  //modal
  const [modal, setModal] = useState(false);
  const [checkoutModalOpen, setCheckoutModal] = useState(false);
  const dots = [];
  //location, id
  const history = useHistory();
  const location = useLocation();
  const gender = location.pathname.split("/")[1];
  const pathId = location.pathname.split("/")[2];
  const adminPanel = location.pathname.split("/")[3];
  //window size
  const [size, setSize] = useState([0, 0]);
  const [mv, setMV] = useState(false);

  //getting window size
  useLayoutEffect(() => {
    function updateSize() {
      setSize([window.innerWidth, window.innerHeight]);
    }
    window.addEventListener("resize", updateSize);
    updateSize();
    return () => window.removeEventListener("resize", updateSize);
  }, []);
  // useEffect(() => {
  //   setMV(window.matchMedia("(min-width: 1000px)").matches);
  // }, [size, mv]);
  //dispatch data
  const dispatch = useDispatch();
  //get data back
  const { cart } = useSelector((state) => state.cart);
  const user = JSON.parse(localStorage.getItem("profile"));
  // useEffect(() => {
  //   dispatch(getItem(pathId));
  // }, []);
  const { item, isLoading } = useSelector((state) => state.item);
  //handlers
  const activeImageHandler = (list) => {
    for (let i = 0; i < list; i++) {
      dots.push(i);
    }
  };
  const sizeHandler = (e) => {
    setItemsSize(e.target.value);
  };
  const cartHandler = () => {
    if (itemsSize !== "") {
      setCheckoutModal(!checkoutModalOpen);
      setProceedError(false);
      item.cartAmount = 1;
      item.gender = gender;
      const found = cart.find((i) => i.id === item.id && i.size === itemsSize);
      item.size = itemsSize;
      if (found) {
        dispatch({
          type: "INCREASE",
          payload: {
            id: found.id,
            size: found.size,
            amount: found.cartAmount,
          },
        });
      } else {
        dispatch({
          type: "ADD_TO_CART",
          payload: {
            item: { ...item, size: itemsSize },
          },
        });
      }
    } else {
      setProceedError(true);
    }
  };
  const deleteItemHandler = () => {
    dispatch(deleteItem(item._id, history));
  };
  return (
    <ShowLoading isLoading={isLoading}>
      <>
        <ItemDetailsPageComponent>
          <div className="top-side">
            <div className="left-side">
              {item.images && (
                <div className="images-show">
                  {item.images.map((img, index) => (
                    <SmallImage
                      url={img.url}
                      index={index}
                      key={index}
                      setCurrentIndex={setCurrentIndex}
                      currentIndex={currentIndex}
                    />
                  ))}
                </div>
              )}
              {item.images && (
                <div
                  className="main-image"
                  style={{
                    backgroundImage: `url(${item.images[currentIndex].url})`,
                  }}
                  onClick={() => setModal(!modal)}
                >
                  <ArrowBackIosIcon
                    className="arrows"
                    onClick={(e) => {
                      e.stopPropagation();
                      currentIndex - 1 === -1
                        ? setCurrentIndex(item.images.length - 1)
                        : setCurrentIndex(
                            (currentIndex - 1) % item.images.length
                          );
                    }}
                  />
                  <ArrowForwardIosIcon
                    className="arrows"
                    onClick={(e) => {
                      e.stopPropagation();
                      setCurrentIndex((currentIndex + 1) % item.images.length);
                    }}
                  />
                  <div
                    className="image-count"
                    onClick={(e) => e.stopPropagation()}
                  >
                    {activeImageHandler(item.images.length)}
                    {dots.map((item, index) => (
                      <div
                        key={index}
                        className={
                          index === currentIndex ? "dot active-dot" : "dot"
                        }
                        onClick={(e) => {
                          e.stopPropagation();
                          setCurrentIndex(index);
                        }}
                      ></div>
                    ))}
                  </div>
                </div>
              )}
            </div>
            <div className="right-side">
              <span
                className="delete-button"
                onClick={() => deleteItemHandler()}
              >
                Delete <DeleteIcon className="delete-icon" />
              </span>
              <Link to={`/add-item/${item._id}`} className="edit-button">
                Edit <EditIcon className="edit-icon" />
              </Link>
              <div className="info">
                <span className="name">{item.name}</span>
                <span className="price">
                  {item.discount === true ? (
                    <>
                      <b
                        style={{
                          color: "tomato",
                        }}
                      >
                        {item.price} GBP
                      </b>
                      <p
                        style={{
                          color: "#b3b3b3",
                          textDecoration: "line-through",
                        }}
                      >
                        {item.priceBeforeDiscount} GBP
                      </p>
                    </>
                  ) : (
                    item.price
                  )}{" "}
                </span>
                {proceedError && (
                  <span className="error">You need to choose size first</span>
                )}
                <div className="size">
                  <FormControl variant="outlined" className="select">
                    <InputLabel>Size</InputLabel>

                    {item.category === "shoes" || item.item === "shoes" ? (
                      <Select
                        value={itemsSize}
                        onChange={(e) => sizeHandler(e)}
                        label="Size"
                        error={proceedError ? true : false}
                      >
                        <MenuItem value="36">36</MenuItem>
                        <MenuItem value="37">37</MenuItem>
                        <MenuItem value="38">38</MenuItem>
                        <MenuItem value="39">39</MenuItem>
                        <MenuItem value="40">40</MenuItem>
                        <MenuItem value="41">41</MenuItem>
                        <MenuItem value="42">42</MenuItem>
                        <MenuItem value="43">43</MenuItem>
                        <MenuItem value="44">44</MenuItem>
                      </Select>
                    ) : (
                      <Select
                        value={itemsSize}
                        onChange={(e) => sizeHandler(e)}
                        label="Size"
                        error={proceedError ? true : false}
                      >
                        <MenuItem value="s">S</MenuItem>
                        <MenuItem value="m">M</MenuItem>
                        <MenuItem value="l">L</MenuItem>
                        <MenuItem value="xl">XL</MenuItem>
                        <MenuItem value="xxl">XXL</MenuItem>
                      </Select>
                    )}
                  </FormControl>
                </div>
                <Button
                  label="Add to bag"
                  onClick={() => cartHandler()}
                  Icon={<LocalMallIcon style={{ color: "white" }} />}
                />
              </div>
            </div>
          </div>
          <div className="bottom-side">
            <div className="accordions">
              {item.description && (
                <Accordion className="accordion" defaultExpanded={true}>
                  <AccordionSummary
                    expandIcon={<ExpandMoreIcon />}
                    className="accordion-header"
                  >
                    Description
                  </AccordionSummary>
                  <AccordionDetails> - {item.description}.</AccordionDetails>
                </Accordion>
              )}
              {/* second accordion */}
              {item.materials && (
                <Accordion className="accordion">
                  <AccordionSummary
                    expandIcon={<ExpandMoreIcon />}
                    className="accordion-header"
                  >
                    Material and care
                  </AccordionSummary>

                  <AccordionDetails>Fabric:</AccordionDetails>
                  {item.materials.map((material) => (
                    <AccordionDetails key={material.fabric}>
                      <span>
                        {" "}
                        - {material.percentage}% {material.fabric}
                      </span>
                    </AccordionDetails>
                  ))}
                </Accordion>
              )}
              {/* third accordion */}
              <Accordion className="accordion">
                <AccordionSummary
                  expandIcon={<ExpandMoreIcon />}
                  className="accordion-header"
                >
                  delivery and returns
                </AccordionSummary>
                <AccordionDetails style={{ fontWeight: "bold" }}>
                  Shipping Policy:
                </AccordionDetails>
                <AccordionDetails className="shipping-details">
                  <div>Free delivery on all orders of 30 or more</div>
                  <div>Standard delivery: 3-9 working days, 3.90GBP</div>
                  <div>Pick up in store: 3-9 working days, free of charge</div>
                  <div>
                    Due to the current epidemiological situation, the delivery
                    time may be extended.
                  </div>
                </AccordionDetails>
                <AccordionDetails style={{ fontWeight: "bold" }}>
                  Returns Policy:
                </AccordionDetails>
                <AccordionDetails className="shipping-details">
                  <div>
                    If products are not what you expected you may return them
                    within 30 days of delivery.
                  </div>
                  <div>
                    - to our online store - fill in the online return form and
                    send the products back to us.
                  </div>
                </AccordionDetails>
              </Accordion>
            </div>
          </div>
          <FullImageModal
            setCurrentIndex={setCurrentIndex}
            currentIndex={currentIndex}
            item={item}
            itemsSize={itemsSize}
            setModal={setModal}
            modal={modal}
          />

          <h1>Recommended</h1>
        </ItemDetailsPageComponent>
        <SimilarItems
          category={item.item}
          gender={gender}
          pathId={pathId}
          id={item.id}
          mv={mv}
        />
        {item && (
          <CheckoutModal
            item={item}
            setCheckoutModal={setCheckoutModal}
            checkoutModalOpen={checkoutModalOpen}
          />
        )}
      </>
    </ShowLoading>
  );
};

