export const signIn = [
  {
    label: "Email",
    name: "email",
    type: "email",
    required: true,
  },
  {
    label: "Password",
    name: "password",
    type: "password",
    required: true,
    useHandler: true,
  },
];
export const signUp = [
  {
    label: "Email",
    name: "email",
    type: "email",
    required: true,
  },
  {
    label: "Password",
    name: "password",
    type: "password",
    required: true,
  },
  {
    label: "Name",
    name: "name",
    type: "name",
    required: false,
  },
  {
    label: "Surname",
    name: "surname",
    type: "surname",
    required: false,
    useHandler: true,
  },
];
export const accountInputs = [
  {
    label: "Name",
    name: "name",
    type: "name",
    required: false,
  },
  {
    label: "Surname",
    name: "surname",
    type: "surname",
    required: false,
  },
  {
    label: "Email",
    name: "email",
    type: "email",
    required: true,
  },
];
export const passwordInputs = [
  {
    label: "Your password",
    name: "password",
    type: "password",
    required: true,
  },
  {
    label: "New password",
    name: "newPassword",
    type: "password",
    required: true,
  },
  {
    label: "Confirm password",
    name: "confirmPassword",
    type: "password",
    required: true,
  },
];
export const addressInputs = [
  {
    label: "Street",
    name: "street",
    type: "text",
    required: true,
  },
  {
    label: "House nr",
    name: "houseNr",
    type: "text",
    required: true,
  },
  {
    label: "City",
    name: "city",
    type: "text",
    required: true,
  },
  {
    label: "Postal code",
    name: "postalCode",
    type: "text",
    required: true,
  },
  {
