import React, { useState, useEffect, useLayoutEffect } from "react";
//styling
import styled from "styled-components";
//icons
import FavoriteBorderIcon from "@material-ui/icons/FavoriteBorder";
//link
import { Link } from "react-router-dom";
//notistack
import { useSnackbar } from "notistack";
//redux
import { useDispatch, useSelector } from "react-redux";
//actions
import { addToFavorites } from "../actions/userActions";

const Card = ({
  item,
  lgHeight,
  smHeight,
  smWidth,
  lgWidth,
  smMargin,
  lgMargin,
  id,
  adminPanel,
}) => {
  //state
  const { user, isLoading } = useSelector((state) => state.user);
  const [favorite, setFavorite] = useState(false);
  const dispatch = useDispatch();
  useEffect(() => {
    if (!isLoading) {
      setFavorite(user.favorites.find((favorite) => favorite === String(id)));
    }
  }, [isLoading, user, id]);
  //snack bar
  const { enqueueSnackbar, closeSnackbar } = useSnackbar();
  // handlers
  const snackbarHandler = (snackbarMessage, snackVariant) => {
    enqueueSnackbar(snackbarMessage, { variant: snackVariant });
    closeSnackbar(500);
  };
  const favoritesHandler = () => {
    if (!favorite) {
      dispatch(addToFavorites(user._id, id));
      setFavorite(true);
      snackbarHandler("Added to favorites", "success");
    } else {
      dispatch(addToFavorites(user._id, id));
      setFavorite(false);
      snackbarHandler("Removed from favorites", "error");
    }
  };
  return (
    <CardComponent>
      {user && (
        <FavoriteBorderIcon
          className="favoriteIcon"
          style={{ color: favorite ? "red" : "rgba(0, 0, 0, 0.2)" }}
          onClick={() => favoritesHandler()}
        />
      )}
      <Link
        to={adminPanel ? `/items/${item._id}/admin` : `/items/${item._id}`}
        className="link"
        onClick={() => window.scrollTo(0, 0)}
      >
        <img
          src={item?.images[0]?.url}
          alt={item.name}
          onMouseOver={(e) => (e.currentTarget.src = `${item?.images[1]?.url}`)}
          onMouseOut={(e) => (e.currentTarget.src = `${item?.images[0]?.url}`)}
        />

        <div className="name">{item.name}</div>
      </Link>
      <div className="price">
        {item.discount === "true" || item.discount === true ? (
          <p>
            {item.price} GBP
            <span style={{ textDecoration: "line-through" }}>
              {item.priceBeforeDiscount} GBP
            </span>
          </p>
        ) : (
          <span>{item.price} GBP</span>
        )}
      </div>
    </CardComponent>
  );
};

const CardComponent = styled.div`
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
  height: 30rem;
  .link {
    text-align: center;
  }
  img {
    width: 100%;
    height: 25rem;
    object-fit: cover;
    object-position: center;
  }
  .price {
    font-size: 0.8rem;
    display: flex;
    @media screen and (max-width: 1000px) {
      font-size: 0.7rem;
    }
    p {
      color: red;
      padding: 0;
      margin: 0;
    }
    span {
      padding: 0px 5px;
      color: black;
    }
  }
  .name {
    height: 1rem;
    padding: 2px 0;
    font-weight: bold;
    font-size: 0.8rem;
    @media screen and (max-width: 1000px) {
      font-size: 0.6rem;
      height: 1.3rem;
    }
  }
  .favoriteIcon {
    position: absolute;
    top: 0;
    right: 0;
    z-index: 2;
    font-size: 3rem;
    margin: 2rem 1rem;
    &:hover {
      cursor: pointer;
    }
    @media screen and (max-width: 1000px) {
      margin: 0.5rem;
      font-size: 2rem;
    }
  }
`;

export default Card;
