import React from "react";
//styling
import styled from "styled-components";
//material ui
import Input from "./Input";
//icons
import CloseIcon from "@material-ui/icons/Close";
import ArrowBackIosIcon from "@material-ui/icons/ArrowBackIos";
//data
import { addressInputs } from "../descriptions/inputs";
//components
import Button from "./Button";

const AddressModal = ({
  addressData,
  handleForm,
  setChosenAddress,
  areInputsEmpty,
  setAddressData,
  snackbarHandler,
  setModal,
}) => {
  //setting address
  const anonymousAddressHandler = () => {
    if (areInputsEmpty()) {
      setModal(false);
      setChosenAddress(true);
      snackbarHandler("address added successfully", "success");
    } else {
      snackbarHandler("Inputs can`t be empty", "error");
    }
  };
  const goBackHandler = () => {};
  return (
    <AddressComponent>
      <div className="anonymous-address">
        <div className="header">
          <span onClick={() => goBackHandler()}>
            {" "}
            <ArrowBackIosIcon />
            Go back
          </span>
          <span>Add</span>
          <CloseIcon className="close-modal" onClick={() => setModal(false)} />
        </div>
        <div className="inputs">
          {addressInputs.map((input) => (
            <Input
              key={input.label}
              label={input.label}
              name={input.name}
              value={addressData[input.name]}
              type={input.type}
              handleChange={(e) => handleForm(e, setAddressData, addressData)}
              required={input.required}
            />
          ))}
        </div>
        <Button
          label="save"
          variant="black"
          onClick={() => anonymousAddressHandler()}
        >
          Save
        </Button>
      </div>
    </AddressComponent>
  );
};

const AddressComponent = styled.div`
  position: absolute;
  z-index: 5;
  background-color: rgba(0, 0, 0, 0.4);
  left: 0;
  top: 0;
  height: 100%;
  width: 100%;
  .anonymous-address {
    background-color: white;
    margin-left: 40%;
    margin-top: 10%;
    width: 25%;
    justify-content: center;
    align-items: center;
    padding: 1rem;
    @media screen and (max-width: 1000px) {
      width: 100%;
      margin: 0;
    }
    .header {
      display: flex;
      justify-content: space-between;
      width: 100%;
      padding: 1rem;
      span {
        &:hover {
          cursor: pointer;
        }
      }
    }
    .inputs {
      display: flex;
      flex-direction: column;
      align-items: center;
      width: 100%;
      padding: 1rem;
      .input {
        width: 100%;
      }
      .two-inputs {
        width: 100%;
        display: flex;
      }
      .postal {
        margin-right: 10px;
      }
    }
  }
`;

export default AddressModal;
