import React, { useState, useEffect } from "react";
//react router
import { useLocation } from "react-router";
import { Link, useHistory } from "react-router-dom";
//redux
import { useDispatch, useSelector } from "react-redux";
//actions
import { loadUsersOrders } from "../actions/ordersAction";
//styling
import styled from "styled-components";
//material ui
import Breadcrumbs from "@material-ui/core/Breadcrumbs";
//icons
import ShoppingBasketIcon from "@material-ui/icons/ShoppingBasket";
import AccountBoxIcon from "@material-ui/icons/AccountBox";
import MeetingRoomIcon from "@material-ui/icons/MeetingRoom";
import HomeIcon from "@material-ui/icons/Home";
//data
import { accountInputs, passwordInputs } from "../descriptions/inputs";
//components
import Order from "../components/Order";
import Input from "../components/Input";
import Button from "../components/Button";
import AddressComponent from "../components/AddressComponent";

const AccountPage = () => {
  const [deleteInfo, setDeleteInfo] = useState(false);
  //users State
  const [userData, setUserData] = useState({
    name: "",
    surname: "",
    email: "",
    phone: "",
  });
  const [passwordData, setPasswordData] = useState({
    password: "",
    newPassword: "",
    confirmPassword: "",
  });
  const [order, setOrder] = useState([]);
  //account state
  const [passwordErrorMsg, setPasswordErrorMsg] = useState("");
  const [accountErrorMsg, setAccountErrorMsg] = useState("");
  //use
  const location = useLocation();
  const history = useHistory();
  const dispatch = useDispatch();
  //pathName
  const pathName = location.pathname.split("/")[3];
  const orderDetails = location.pathname.split("/")[4];
  const { user, isLoading } = useSelector((state) => state.user);
  console.log(user);
  useEffect(() => {
    if (user) {
      dispatch(loadUsersOrders(user.id));
    }
  }, [dispatch, user]);
  const { userOrders } = useSelector((state) => state.orders);

  //handlers
  const userAccountHandler = () => {};
  const passwordChangeHandler = () => {};
  const deleteAccountHandler = () => {};

  const LogOutHandler = () => {
    localStorage.removeItem("userId");
    history.push("/customer/account/login");
  };
  const orderDetailsHandler = (id, order) => {
    history.push(`/customer/account/orders/${id}`);
    setOrder(order);
    window.scrollTo(0, 0);
  };
  const handleForm = (e, setData, data) =>
    setData({ ...data, [e.target.name]: e.target.value });
  return (
    <>
      {user && (
        <LoggedInComponent>
          <div className="left-side">
            <ul>
              <Link to="/customer/account/orders" className="link">
                <li className={pathName === "orders" ? "active" : ""}>
                  <ShoppingBasketIcon /> Orders
                </li>
              </Link>
              <Link to="/customer/account/address" className="link">
                <li className={pathName === "address" ? "active" : ""}>
                  <HomeIcon /> Address data
                </li>
              </Link>

              <Link to="/customer/account/info" className="link">
                <li className={pathName === "info" ? "active" : ""}>
                  <AccountBoxIcon /> Account info
                </li>
              </Link>
              <li className="log-out" onClick={() => LogOutHandler()}>
                <MeetingRoomIcon /> Log out
              </li>
            </ul>
          </div>
          {user && (
            <div className="right-side">
              {pathName === "info" && (
                <div className="infoComponent">
                  <div className="info">
                    <span>My account</span>
                    <div className="line"></div>
                    <span>{accountErrorMsg}</span>
                    {accountInputs.map((input) => (
                      <span>
                        <Input
                          label={input.label}
                          name={input.name}
                          value={userData[input.name]}
                          type={input.type}
                          handleChange={(e) =>
                            handleForm(e, setUserData, userData)
                          }
                          required={input.required}
                        />
                      </span>
                    ))}
                    <Button
                      onClick={() => userAccountHandler()}
                      label="Save changes"
                      variant="black"
                    />
                    <div className="line"></div>
                    <div className="password-change">
                      <span>Password change</span>
                      <span>{passwordErrorMsg}</span>
                      {passwordInputs.map((input) => (
                        <Input
                          label={input.label}
                          name={input.name}
                          value={passwordData[input.name]}
                          type={input.type}
                          handleChange={(e) =>
                            handleForm(e, setPasswordData, passwordData)
                          }
                          required={input.required}
                        />
                      ))}
                      <Button
                        onClick={() => passwordChangeHandler()}
                        label="Change password"
                        variant="black"
                      />
                    </div>
                    <div className="line"></div>
                    <div className="delete-acc">
                      <span onClick={() => setDeleteInfo(true)}>
                        Delete account
                      </span>
                      <div
                        style={{ display: deleteInfo ? "block" : "none" }}
                        className="delete-confirmation"
                      >
                        <span>Are you sure you want to delete account?</span>
                        <div className="buttons">
                          <Button
                            onClick={() => deleteAccountHandler()}
                            label="Yes"
                          />
                          <Button
                            onClick={() => setDeleteInfo(false)}
                            label="No"
                            variant="black"
                          />
                        </div>
                      </div>
                    </div>
                    <div className="line"></div>
                  </div>
                </div>
              )}
              {pathName === "orders" && !orderDetails && (
                <div className="orders">
                  {userOrders.length > 0 ? (
                    <>
                      <Breadcrumbs aria-label="breadcrumb">
                        <span>My account</span>
                        <Link to="/customer/account/orders" className="link">
                          My orders
                        </Link>
                      </Breadcrumbs>
                      {userOrders.map((order) => (
                        <div className="order" key={order.id}>
                          <div className="order-left">
                            <div className="price-info">
                              <span>Nr:{order.id}</span>
                              <span>
                                <b>
                                  {(
                                    order.cartPrice + order.deliveryPrice
                                  ).toFixed(2)}{" "}
                                  GBP
                                </b>
                              </span>
                            </div>
                            <div className="items-image">
                              {order.items.map((item) => (
                                <img
                                  src={item.images[0].img}
                                  alt={item.name}
                                  key={item.id}
                                  onClick={() =>
                                    orderDetailsHandler(order.id, order)
                                  }
                                />
                              ))}
                            </div>
                          </div>
                          <div className="order-right">
                            <div className="time-info">
                              <span>
                                {order.date} {order.time}
                              </span>
                              <span>{order.status}</span>
                            </div>
                            <div className="details">
                              <span
                                className="details-button"
                                onClick={() =>
                                  orderDetailsHandler(order.id, order)
                                }
                              >
                                Details
                              </span>
                            </div>
                          </div>
                        </div>
                      ))}
                    </>
                  ) : (
                    <div className="no-orders">
                      <h2>You didnt order anything</h2>
                      <span>Go and explore</span>
                      <div className="buttons">
                        <Link to="/woman">
                          <Button label="Women" />
                        </Link>
                        <Link to="/man">
                          <Button label="Men" />
                        </Link>
                      </div>
                    </div>
                  )}
                </div>
              )}
              {orderDetails && (
                <div className="specific-order">
                  <Breadcrumbs aria-label="breadcrumb">
                    <span>My account</span>
                    <Link to="/customer/account/orders" className="link">
                      My orders
                    </Link>
                    <span>{order.id}</span>
                  </Breadcrumbs>
                  <Order />
                </div>
              )}
              {pathName === "address" && (
                <AddressComponent user={user} isLoading={isLoading} />
              )}
            </div>
          )}
        </LoggedInComponent>
      )}
    </>
  );
};

const LoggedInComponent = styled.div`
  display: flex;
  margin-top: 2rem;
  min-height: 70vh;
  @media screen and (max-width: 1000px) {
    flex-direction: column;
  }
  .left-side {
    width: 20%;
    display: flex;
    align-items: flex-end;
    flex-direction: column;
    font-size: 1.5rem;
    @media screen and (max-width: 1000px) {
      width: 100%;
      align-items: flex-start;
      margin: 0rem 0.5rem;
    }
    .active {
      font-weight: bold;
    }
    ul {
      list-style: none;
      li {
        &:hover {
          color: rgba(0, 0, 0, 0.6);
        }
      }
      .log-out {
        &:hover {
          cursor: pointer;
          color: #e0431c;
        }
      }
    }
  }
  .right-side {
    width: 60%;
    justify-content: center;
    align-items: Center;

    @media screen and (max-width: 1000px) {
      width: 100%;
      margin: 0rem 0.5rem;
    }
    .infoComponent {
      display: flex;
      flex-direction: column;
      margin-left: 2rem;
      @media screen and (max-width: 1000px) {
        margin: 0rem;
      }
      .info {
        display: flex;
        flex-direction: column;
        text-align: left;
        .input {
          width: 15rem;
        }
        .password-change {
          display: flex;
          flex-direction: column;
        }
        .delete-acc {
          .delete-confirmation {
            display: flex;
            flex-direction: column;
            margin: 1rem 0rem;
          }
        }
      }
    }
    .line {
      width: 100%;
      height: 1px;
      background-color: black;
      margin: 2rem 0rem;
      @media screen and (max-width: 1000px) {
        margin: 1rem 0;
        width: 90%;
      }
    }
    .inputs-grid {
      display: grid;
      grid-template-columns: 1fr 1fr;
    }
    .no-orders {
      display: flex;
      flex-direction: column;
      text-align: center;
      .buttons {
        display: flex;
        align-items: center;
        justify-content: center;
      }
    }
    font-size: 2rem;
    .orders {
      margin-left: 2rem;
      @media screen and (max-width: 1000px) {
        margin-left: 0;
      }
      .order {
        display: flex;
        justify-content: space-between;
        font-size: 1.5rem;
        border-bottom: 1px solid rgba(0, 0, 0, 0.2);
        padding: 1rem;

        .order-left {
          display: flex;
          flex-direction: column;
          justify-content: space-between;
          .price-info {
            display: flex;
            flex-direction: column;
            font-size: 1rem;
          }
          .items-image {
            display: flex;
            flex-wrap: wrap;
            img {
              margin: 0.5rem 0.5rem 0.5rem 0;
              height: 10rem;
              width: 8rem;
            }
          }
        }
        .order-right {
          display: flex;
          flex-direction: column;
          justify-content: space-between;
          .time-info {
            display: flex;
            flex-direction: column;
            font-size: 1rem;
            span {
              align-self: flex-end;
            }
          }
          .details {
            align-self: flex-end;
            span {
              padding: 0.6rem;
              font-size: 1rem;
              font-weight: bold;
              text-transform: upperCase;
              &:hover {
                background-color: rgba(0, 0, 0, 0.2);
                cursor: pointer;
              }
            }
          }
        }
      }
    }
    .specific-order {
      margin-left: 2rem;
      display: flex;
      flex-direction: column;
      font-size: 1.5rem;
      @media screen and (max-width: 1000px) {
        margin-left: 0;
      }
    }
  }
`;
export default AccountPage;
