//notistack
import { useSnackbar } from "notistack";

export default (snackbarMessage, snackVariant) => {
  const { enqueueSnackbar, closeSnackbar } = useSnackbar();
  enqueueSnackbar(snackbarMessage, { variant: snackVariant });
  closeSnackbar(500);
};
