import React, { useEffect, useState } from "react";
//styling
import styled from "styled-components";
//router
import { useLocation } from "react-router";
import { Link, useHistory } from "react-router-dom";
//actions

import { getOrders } from "../actions/ordersAction";
import { getUsers } from "../actions/userActions";
//redux
import { useDispatch, useSelector } from "react-redux";
//material ui
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import TextField from "@material-ui/core/TextField";
import InputAdornment from "@material-ui/core/InputAdornment";
import Pagination from "@material-ui/lab/Pagination";
import Breadcrumbs from "@material-ui/core/Breadcrumbs";
//icons
import SearchIcon from "@material-ui/icons/Search";
//components
import UserDetails from "../components/UserDetails";
import ItemsComponent from "../components/ItemsComponent";
import OrdersComponent from "./OrdersComponent";
//notistack
import { useSnackbar } from "notistack";

const AdminPanel = () => {
  const dispatch = useDispatch();
  //state
  const { enqueueSnackbar, closeSnackbar } = useSnackbar();
  const { user } = useSelector((state) => state.user);
  const { users } = useSelector((state) => state.user);
  const { orders } = useSelector((state) => state.orders);
  const location = useLocation();
  const history = useHistory();
  const pathName = location.pathname.split("/")[3];
  const details = location.pathname.split("/")[4];
  //orders state
  const [ordersDate, setOrdersDate] = useState("");
  const [ordersPage, setOrdersPage] = useState(1);
  const [ordersPageIndex, setOrdersPageIndex] = useState(1);
  //users state
  const [userId, setUserId] = useState("");
  const [usersAccess, setUsersAccess] = useState("customer");
  const [usersSearch, setUsersSearch] = useState("");
  const [usersPage, setUsersPage] = useState(1);
  const [pageIndex, setPageIndex] = useState(1);
  //useEffect
  useEffect(() => {
    if (user) {
      dispatch(getOrders());
    }
  }, [dispatch]);
  useEffect(() => {
    if (user) {
      dispatch(getUsers(usersSearch, usersAccess));
    }
  }, [dispatch, usersSearch, usersAccess]);
  // useEffect(() => {
  //   if (users.header) {
  //     if (users.header.link) {
  //       setPageIndex(
  //         users.headers.link
  //           .split(",")
  //           [users.headers.link.split(",").length - 1].split(";")[0]
  //           .split("&")[3]
  //           .split("=")[1]
  //           .split("")[0]
  //       );
  //     }
  //   }
  // }, [user, setPageIndex]);
  // useEffect(() => {
  //   if (orders.header) {
  //     if (orders.header.link) {
  //       setOrdersPageIndex(
  //         users.headers.link
  //           .split(",")
  //           [users.headers.link.split(",").length - 1].split(";")[0]
  //           .split("&")[3]
  //           .split("=")[1]
  //           .split("")[0]
  //       );
  //     }
  //   }
  // }, [users, setPageIndex, isLogged, orders.header]);
  //handlers
  const snackbarHandler = (snackbarMessage, snackVariant) => {
    enqueueSnackbar(snackbarMessage, { variant: snackVariant });
    closeSnackbar(500);
  };
  const ordersSortHandler = (e) => {
    setOrdersDate(e.target.value);
  };
  const sortAccessHandler = (e) => {
    setUsersAccess(e.target.value);
  };
  const handleUsersPage = (e, v) => {
    setUsersPage(v);
    window.scrollTo(0, 0);
  };
  const handleOrdersPage = (e, v) => {
    setOrdersPage(v);
    window.scrollTo(0, 0);
  };
  const userDetailsHandler = (id) => {
    history.push(`/admin/panel/users/${id}`);
    setUserId(id);
    window.scrollTo(0, 0);
  };
  return (
    <AdminPanelComponent>
      <div className="left-side">
        <ul>
          <Link to="/admin/panel/orders" className="link">
            <li>Manage Orders</li>
          </Link>
          <Link to="/admin/panel/items" className="link">
            <li>Manage Items</li>
          </Link>
          {user.role === "headAdmin" && (
            <Link to="/admin/panel/users" className="link">
              <li>Manage users</li>
            </Link>
          )}
          <Link to="/add-item" className="link">
            <li>Add Item</li>
          </Link>
        </ul>
      </div>
      <div className="right-side">
        {pathName === "orders" && (
          <OrdersComponent
            orderDetails={details}
            title="Admin panel"
            link={{ label: "orders", path: "/admin/panel/orders" }}
            orders={orders}
            snackbarHandler={snackbarHandler}
          />
        )}
        {pathName === "users" && !details && user.role === "headAdmin" && (
          <div className="users-component">
            <div className="sort-users">
              <FormControl>
                <InputLabel className="sort-label">Show only</InputLabel>
                <Select
                  value={usersAccess}
                  onChange={sortAccessHandler}
                  className="sort-select"
                >
                  <MenuItem value="customer">customer</MenuItem>
                  <MenuItem value="admin">admin</MenuItem>
                  <MenuItem value="headAdmin">Head admin</MenuItem>
                </Select>
              </FormControl>
              <div className="search-users">
                <TextField
                  label="search"
                  value={usersSearch}
                  className="users-input"
                  onChange={(e) => setUsersSearch(e.target.value)}
                  InputProps={{
                    startAdornment: (
                      <InputAdornment position="start">
                        <SearchIcon />
                      </InputAdornment>
                    ),
                  }}
                />
              </div>
            </div>
            <table className="users-table">
              <tbody>
                <tr>
                  <th>Name</th>
                  <th>Email</th>
                  <th>Role</th>
                  <th>More info</th>
                </tr>

                {users?.length > 0 &&
                  users?.map((user, index) => (
                    <tr
                      key={user.id}
                      style={{
                        backgroundColor:
                          index % 2 ? "rgba(0,0,0,0.4)" : "white",
                      }}
                    >
                      <td>
                        {user.name} {user.surname}
                      </td>
                      <td>{user.email}</td>
                      <td>{user.role}</td>
                      <td
                        onClick={() => userDetailsHandler(user._id, user)}
                        style={{ cursor: "pointer" }}
                      >
                        Details
                      </td>
                    </tr>
                  ))}
              </tbody>
            </table>
            <Pagination
              count={parseInt(pageIndex)}
              page={usersPage}
              onChange={handleUsersPage}
              className="pagination"
            />
          </div>
        )}
        {details && pathName === "users" && (
          <>
            <Breadcrumbs aria-label="breadcrumb">
              <span>Admin panel</span>
              <Link to="/admin/panel/users" className="link">
                users
              </Link>
              <span>{userId}</span>
            </Breadcrumbs>
            <UserDetails />
          </>
        )}
        {pathName === "items" && <ItemsComponent />}
      </div>
    </AdminPanelComponent>
  );
};

