import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
//styling
import styled from "styled-components";
import { getFavorites } from "../actions/itemsAction";
//components
import Card from "../components/Card";
import ShowLoading from "../components/ShowLoading";
import Pagination from "../components/Pagination";

const FavoritesPage = () => {
  const { user, isLoading, numberOfPages } = useSelector((state) => state.user);
  const { favorites } = useSelector((state) => state.item);
  const [page, setPage] = useState(1);
  const dispatch = useDispatch();
  console.log(favorites);
  useEffect(() => {
    if (!isLoading) dispatch(getFavorites(user._id, page));
  }, [dispatch, user._id, page, isLoading]);

  return (
    <FavoritesPageComponent>
      <ShowLoading isLoading={isLoading}>
        <div className="header">
          <h2>Favorites</h2>
          {favorites && (
            <span className="products-amount">
              {favorites.length} {favorites.length === 1 ? "item" : "items"}
            </span>
          )}
        </div>
        <div className="items-display">
          {favorites?.length > 0
            ? favorites?.map((item) => (
                <Card
                  key={item._id}
                  lgHeight={"30rem"}
                  smHeight={"20rem"}
                  smWidth={"48%"}
                  lgWidth={"24%"}
                  lgMargin={"1rem 0.3rem"}
                  smMargin={"0.5rem 0.1px"}
                  id={item._id}
                  item={item}
                />
              ))
            : "Add item to favorites"}
        </div>
        <Pagination
          page={page}
          numberOfPages={numberOfPages}
          setPage={setPage}
        />
      </ShowLoading>
    </FavoritesPageComponent>
  );
};

const FavoritesPageComponent = styled.div`
  min-height: 90vh;
  display: flex;
  flex-direction: column;
  margin-top: 2rem;
  align-items: center;
  .header {
    width: 80vw;
    position: relative;
    display: flex;
    text-align: Center;
    justify-content: center;
    @media screen and (max-width: 1000px) {
      width: 100%;
    }
    h2 {
      @media screen and (max-width: 1000px) {
        font-size: 1.5rem;
      }
    }
    .products-amount {
      position: absolute;
      right: 0;
      top: 0;
      margin: 0rem 0.5rem;
    }
  }
  .items-display {
    margin-top: 2rem;
    width: 80%;
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
    @media screen and (max-width: 1000px) {
      width: 100%;
    }
  }
`;

export default FavoritesPage;
