import React, { useEffect, useState } from "react";
//styling
import styled from "styled-components";
//history
import { useHistory } from "react-router-dom";
//redux
import { useDispatch, useSelector } from "react-redux";
//axios
import axios from "axios";
//material ui
import Accordion from "@material-ui/core/Accordion";
import AccordionSummary from "@material-ui/core/AccordionSummary";
import AccordionDetails from "@material-ui/core/AccordionDetails";
//icons
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
//components
import AddressModal from "../components/AddressModal";
import NewAddressModal from "../components/NewAddressModal";
import EditAddressModal from "../components/EditAddressModal";
import AnonymousAddressModal from "../components/AnonymousAddressModal";
//components
import Button from "../components/Button";
import CartItem from "../components/CartItem";

const CheckoutPage = () => {
  //state
  const dispatch = useDispatch();
  const history = useHistory();
  const { cartPrice, cart } = useSelector((state) => state.cart);
  //delivery state
  const [deliveryType, setDeliveryType] = useState({
    standard: false,
    dhl: false,
  });
  const [deliveryPrice, setDeliveryPrice] = useState(0);
  const [addressModal, setModal] = useState(false);
  const [chosenAddress, setChosenAddress] = useState(false);
  const [delivery, setDelivery] = useState("");
  //payment state
  const [paymentType, setPaymentType] = useState({
    card: false,
    blik: false,
    cash: false,
  });
  const [chosenPayment, setChosenPayment] = useState(false);
  const [payment, setPayment] = useState("");
  //address state
  const [addressData, setAddressData] = useState({
    city: "",
    postalCode: "",
    street: "",
    phone: "",
    houseNr: "",
  });
  const [addressId, setAddressId] = useState("");
  //new address state
  const [addAddressModal, setAddAddressModal] = useState(false);
  //edit modal
  const [editModal, setEditModal] = useState(false);
  //anonymous
  const [anonymousModal, setAnonymousModal] = useState(false);
  const appLink = `https://benzen-server.herokuapp.com`;
  //useEffect
  const user = JSON.parse(localStorage.getItem("profile"));
  console.log(cart);
  const addressOptionHandler = (option, price) => {
    setDeliveryPrice(price);
    setDelivery(option);
    user ? setModal(true) : setAnonymousModal(true);
  };
  const paymentOptionHandler = (option) => {
    setChosenPayment(true);
    setPayment(option);
  };
  const modalHandler = () => {
    setAddAddressModal(!addAddressModal);
    setModal(!addressModal);
  };
  const editModalHandler = () => {
    setModal(!addressModal);
    setEditModal(!editModal);
  };
  //this function checks if cart isn't empty and payment has been chosen
  //then if its true it decreases items amount
  //then function posts new order to database
  const finalizeOrderHandler = () => {
    if (cart.length !== 0 && chosenPayment) {
      history.push("/checkout/order/finalized");
      window.location.reload();
    }
  };
  const handleForm = (e, setData, data) =>
    setData({ ...data, [e.target.name]: e.target.value });
  return (
    <CheckoutPageComponents>
      <div className="left-side">
        <h2>1. Delivery method</h2>
        <div className="delivery">
          <DeliveryOption
            option={"dhl"}
            addressData={addressData}
            label="Standard Delivery"
            chosenAddress={chosenAddress}
            addressOptionHandler={addressOptionHandler}
            deliveryPrice={2.99}
            img="https://jakimkurierem.pl/logo_kuriera/dhl_logo.svg"
          />
          <DeliveryOption
            option={"standard"}
            addressData={addressData}
            label="DHL Delivery"
            chosenAddress={chosenAddress}
            addressOptionHandler={addressOptionHandler}
            deliveryPrice={2.99}
            img="https://image.freepik.com/darmowe-wektory/kurier-z-paczka-na-tle-samochodu-dostawczego_165429-45.jpg"
          />
        </div>
        <h2>2. Payment method</h2>
        <div
          className="payment"
          style={{ display: chosenAddress ? "block" : "none" }}
        >
          <paymentOption
            option="blik"
            label="Blik"
            payment={payment}
            paymentOptionHandler={paymentOptionHandler}
            img="https://www.aliorbank.pl/dam/jcr:5950c2d4-7908-47b8-bd57-0229b44b4901/blik.png"
          />
          <paymentOption
            option="card"
            label="Card (3DSecure cards only)"
            payment={payment}
            paymentOptionHandler={paymentOptionHandler}
            img="https://krakowski-centus.pl/wp-content/uploads/2020/06/visa_mastercard_logo.jpg"
          />
          <paymentOption
            option="cash"
            label="Cash on delivery (cash, credit card, blik)"
            payment={payment}
            paymentOptionHandler={paymentOptionHandler}
            img="https://static.vecteezy.com/system/resources/previews/001/269/599/non_2x/a-wallet-full-of-cash-vector.jpg"
          />
        </div>
      </div>
      <div className="right-side">
        <div className="price-info">
          <div className="items-price">
            <span>products price:</span>
            <span>{cartPrice} GBP</span>
          </div>
          <div className="delivery-price">
            <span>delivery price:</span>
            <span>{deliveryPrice} GBP</span>
          </div>
          <div className="total-price">
            <span>
              <b>total</b> with vat:
            </span>
            <span>
              {(parseFloat(cartPrice) + deliveryPrice).toFixed(2)} GBP
            </span>
          </div>
        </div>
        <Button
          variant={chosenPayment ? "black" : ""}
          disabled={chosenPayment}
          onClick={() => finalizeOrderHandler()}
          label="Buy and pay"
          size="lg"
        />
        <p>
          When you click Shop and Pay you will create your order. When you order
          at benzen, you agree to our Terms and Conditions.
        </p>
        <div className="your-order">
          <Accordion className="accordion">
            <AccordionSummary
              expandIcon={<ExpandMoreIcon />}
              className="accordion-header"
            >
              Your Order <span className="cart-length">{cart.length} QTY</span>
            </AccordionSummary>
            {cart.map((item) => (
              <AccordionDetails key={item.id} className="accordion-detail">
                <img src={item.images[0].url} alt={item.name} />
                <div className="item-info">
                  <div className="item-name">
                    <span>{item.name}</span>
                    {item.cartAmount > 1 && (
                      <span className="amount">amount: {item.cartAmount}</span>
                    )}
                  </div>
                  <div className="item-size">
                    <span>Size: {item.size}</span>
                    {item.discount === "true" || item.discount === true ? (
                      <span style={{ color: "red", fontWeight: "bold" }}>
                        {parseFloat(item.price * item.cartAmount).toFixed(2)}{" "}
                        GBP{" "}
                        <b
                          style={{
                            textDecoration: "line-through",
                            fontWeight: "normal",
                          }}
                        >
                          {(item.beforeDiscount * item.cartAmount).toFixed(2)}{" "}
                          GBP
                        </b>
                      </span>
                    ) : (
                      <span>
                        {(item.price * item.cartAmount).toFixed(2)} GBP
                      </span>
                    )}
                  </div>
                </div>
              </AccordionDetails>
            ))}
          </Accordion>
        </div>
      </div>
      <AddressModal
        addressModal={addressModal}
        setModal={setModal}
        modalHandler={modalHandler}
        editModalHandler={editModalHandler}
        name={name}
        surname={surname}
        phone={phone}
        city={city}
        street={street}
        houseNr={houseNr}
        postalCode={postalCode}
        setName={setName}
        setSurname={setSurname}
        setCity={setCity}
        setPostalCode={setPostalCode}
        setStreet={setStreet}
        setPhone={setPhone}
        setHouseNr={setHouseNr}
        setAddressId={setAddressId}
        setChosenAddress={setChosenAddress}
        addressId={addressId}
      />
      <NewAddressModal
        appLink={appLink}
        addAddressModal={addAddressModal}
        setAddAddressModal={setAddAddressModal}
        modalHandler={modalHandler}
      />

      <EditAddressModal
        editModal={editModal}
        setModal={setModal}
        editModalHandler={editModalHandler}
        setEditModal={setEditModal}
        name={name}
        surname={surname}
        street={street}
        houseNr={houseNr}
        postalCode={postalCode}
        city={city}
        phone={phone}
        setName={setName}
        setSurname={setSurname}
        setHouseNr={setHouseNr}
        setStreet={setStreet}
        setPostalCode={setPostalCode}
        setCity={setCity}
        setPhone={setPhone}
        addressId={addressId}
        appLink={appLink}
      />
      <AnonymousAddressModal
        addressData={addressData}
        handleForm={handleForm}
        anonymousModal={anonymousModal}
        setAnonymousModal={setAnonymousModal}
        setEmail={setEmail}
        setChosenAddress={setChosenAddress}
      />
    </CheckoutPageComponents>
  );
};

const CheckoutPageComponents = styled.div`
  display: flex;
  min-height: 80vh;
  @media screen and (max-width: 1000px) {
    flex-direction: column;
  }
  .left-side {
    width: 60%;
    padding-left: 10vh;
    h2 {
      padding: 1rem 0;
    }
    @media screen and (max-width: 1000px) {
      width: 100%;
      padding-left: 0;
    }
  }
  .right-side {
    display: flex;
    justify-content: center;
    align-items: Center;
    flex-direction: column;
    background-color: #f3f3f5;
    width: 40%;
    @media screen and (max-width: 1000px) {
      width: 100%;
      padding: 1rem 0rem;
    }
    .price-info {
      display: flex;
      justify-content: center;
      align-items: center;
      flex-direction: column;
      width: 100%;
      .items-price,
      .delivery-price,
      .total-price {
        width: 70%;
        display: flex;
        justify-content: space-between;
        @media screen and (max-width: 1000px) {
          width: 100%;
          padding: 8px;
        }
      }
      .total-price {
        padding: 5px 0;
        border-top: 1px solid rgba(0, 0, 0, 0.2);
        @media screen and (max-width: 1000px) {
          padding: 8px;
        }
      }
    }
    p {
      font-size: 0.8rem;
      width: 70%;
      @media screen and (max-width: 1000px) {
        width: 100%;
      }
    }
    .your-order {
      width: 70%;
      @media screen and (max-width: 1000px) {
        width: 100%;
      }
      .accordion {
        background-color: #f3f3f5;
        .accordion-header {
          font-weight: bold;
          .cart-length {
            font-weight: normal;
            margin: 0 5px;
            background-color: white;
          }
        }
        .accordion-detail {
          display: flex;
          width: 100%;
          img {
            height: 8rem;
            width: 6rem;
          }
          .item-info {
            padding: 5px 1rem;
            width: 80%;
            display: flex;
            flex-direction: column;
            justify-content: space-between;
            border-bottom: 1px solid rgba(0, 0, 0, 0.1);
            .item-name {
              font-weight: bold;
              display: flex;
              justify-content: space-between;
              .amount {
                font-weight: normal;
              }
            }
            .item-size {
              display: flex;
              justify-content: space-between;
            }
          }
        }
      }
    }
  }
`;

export default CheckoutPage;
