import axios from "axios";

const API = axios.create({ baseURL: "http://localhost:5000" });

API.interceptors.request.use((req) => {
  if (localStorage.getItem("profile")) {
    req.headers.Authorization = `Bearer ${
      JSON.parse(localStorage.getItem("profile")).token
    }`;
  }
  return req;
});

const appLink = `https://benzen-server.herokuapp.com`;

//ITEMS
export const getItems = (gender) => API.get(`/items?gender=${gender}`);
export const getItemsByItem = (gender, item, order, page) =>
  API.get(`/items/item/${item}?gender=${gender}&order=${order}&page=${page}`);
export const getItemsBySearch = (gender, searchQuery) =>
  API.get(`/items/search?searchQuery=${searchQuery}&gender=${gender}`);
export const getItemsOnSale = (gender, item) =>
  API.get(`/items/sale/${item}?gender=${gender}`);
export const getFavoriteItems = (id, page) =>
  API.get(`/items/favorites/${id}?page=${page}`);
export const getItem = (id) => API.get(`/items/${id}`);
export const createItem = (item) => API.post(`/items`, item);
export const updateItem = (id, item) => API.patch(`/items/update/${id}`, item);
export const decreaseItemAmount = (id) =>
  API.patch(`/items/decreaseAmount/${id}`);
export const deleteItem = (id) => API.delete(`/items/delete/${id}`);

//USER
export const signIn = (formData) => API.post("/user/signin", formData);
export const signUp = (formData) => API.post("/user/signup", formData);
export const getUserById = (id) => API.get(`/user/${id}`);
export const getUsers = (searchQuery, role, page) =>
  API.get(`/user?searchQuery=${searchQuery}&role=${role}&page=${page}`);
export const addToFavorites = (id, itemId) =>
  API.patch(`/user/addToFavorites/${id}`, itemId);
export const addAddress = (id, address) =>
  API.patch(`/user/addAddress/${id}`, address);
export const updateAddress = (id, address, addressId) =>
  API.patch(`/user/updateAddress/${id}`, { address, addressId: addressId });
export const deleteAddress = (userId, addressId) =>
  API.patch(`/user/deleteAddress/${userId}`, { addressId: addressId });
export const updateInfo = (userId, info) =>
  API.patch(`/user/updateInfo/${userId}`, info);
export const updateRole = (userId, role) =>
  API.patch(`/user/updateRole/${userId}`, role);
export const updatePassword = (userId, password) =>
  API.patch(`/user/updatePassword/${userId}`, { password: password });
export const deleteAccount = (userId) =>
  API.delete(`/user/deleteAccount/${userId}`);

//ORDER
export const createOrder = (formData) => API.post("/order", formData);
export const getOrder = (id) => API.get(`/order/${id}`);
export const getOrders = (page) => API.get(`/order/all?page=${page}`);
export const getUserOrders = (id, page) =>
  API.get(`/order/user/${id}?page=${page}`);
export const updateOrder = (id, status) =>
  API.patch(`/order/update/${id}`, { status: status });

export const itemsUrl = (gender, category, item, sortOrder, limit) =>
  `${appLink}/${gender}?category=${category}&item=${item}&_sort=price&_order=${sortOrder}&_limit=${limit} `;

export const specificItemUrl = (gender, id) => `${appLink}/${gender}/${id}`;

export const allItemsUrl = (gender, query, item) =>
  `${appLink}/${gender}?q=${query}&item=${item}`;

export const allUsersUrl = () => `${appLink}/users`;
export const allUsersFilteredUrl = (accessibility, query, page) =>
  `${appLink}/users?accessibility=${accessibility}&q=${query}&_limit=20&_page=${page}`;
export const specificUser = (id) => `${appLink}/users/${id}`;

export const loginUrl = (email) => `${appLink}/users?email=${email}`;

export const registerUrl = (email) => `${appLink}/users?email=${email}`;

export const questionUrl = (gender, question, sortOrder, limit) =>
  `${appLink}/${gender}?q=${question}&_sort=price&_order=${sortOrder}&_limit=${limit}`;

export const fullQuestionLength = (gender, question) =>
  `${appLink}/${gender}?q=${question}`;

export const discountUrl = (gender, item, sortOrder, limit) =>
  `${appLink}/${gender}?item=${item}&discount=true&_sort=price&_order=${sortOrder}&_limit=${limit}`;

export const allOrders = (sortOrder, page) =>
  `${appLink}/orders?_sort=id&_order=${sortOrder}&_limit=20&_page=${page}`;

export const usersOrders = (id) =>
  `${appLink}/orders?usersId=${id}&_sort=id&_order=desc`;
export const specificOrder = (id) => `${appLink}/orders/${id}`;
