import React, { useState, useEffect, useLayoutEffect } from "react";
//styling
import styled from "styled-components";
//material ui
import Accordion from "@material-ui/core/Accordion";
import AccordionSummary from "@material-ui/core/AccordionSummary";
import AccordionDetails from "@material-ui/core/AccordionDetails";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
//router
import { Link } from "react-router-dom";
//redux
import { useDispatch, useSelector } from "react-redux";
//actions
import { getItem, deleteItem } from "../actions/itemsAction";
//router
import { useLocation, useHistory } from "react-router-dom";
//icons
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import ArrowForwardIosIcon from "@material-ui/icons/ArrowForwardIos";
import ArrowBackIosIcon from "@material-ui/icons/ArrowBackIos";
import LocalMallIcon from "@material-ui/icons/LocalMall";
import EditIcon from "@material-ui/icons/Edit";
import DeleteIcon from "@material-ui/icons/Delete";
//components
import SmallImage from "../components/SmallImage";
import CheckoutModal from "../components/CheckoutModal";
import FullImageModal from "../components/FullImageModal";
import SimilarItems from "../components/SimilarItems";
import ShowLoading from "../components/ShowLoading";
import Button from "../components/Button";

const ItemDetailsPage = () => {
  //state
  const [currentIndex, setCurrentIndex] = useState(0);
  const [itemsSize, setItemsSize] = useState("");
  const [proceedError, setProceedError] = useState(false);
  //modal
  const [modal, setModal] = useState(false);
  const [checkoutModalOpen, setCheckoutModal] = useState(false);
  const dots = [];
  //location, id
  const history = useHistory();
  const location = useLocation();
  const gender = location.pathname.split("/")[1];
  const pathId = location.pathname.split("/")[2];
  //window size
  const [size, setSize] = useState([0, 0]);
  const [mv, setMV] = useState(false);

  //getting window size
  useLayoutEffect(() => {
    function updateSize() {
      setSize([window.innerWidth, window.innerHeight]);
    }
    window.addEventListener("resize", updateSize);
    updateSize();
    return () => window.removeEventListener("resize", updateSize);
  }, []);
  useEffect(() => {
    setMV(window.matchMedia("(min-width: 1000px)").matches);
  }, [size, mv]);
  //dispatch data
  const dispatch = useDispatch();
  //get data back
  const { cart } = useSelector((state) => state.cart);
  const { user } = useSelector((state) => state.user);
  useEffect(() => {
    dispatch(getItem(pathId));
  }, []);
  const { item, isLoading } = useSelector((state) => state.item);
  console.log(isLoading);
  //handlers
  // const activeImageHandler = (list) => {
  //   for (let i = 0; i < list; i++) {
  //     dots.push(i);
  //   }
  // };
  // const sizeHandler = (e) => {
  //   setItemsSize(e.target.value);
  // };
  // const cartHandler = () => {
  //   if (itemsSize !== "") {
  //     setCheckoutModal(!checkoutModalOpen);
  //     setProceedError(false);
  //     item.cartAmount = 1;
  //     item.gender = gender;
  //     const found = cart.find((i) => i.id === item.id && i.size === itemsSize);
  //     item.size = itemsSize;
  //     if (found) {
  //       dispatch({
  //         type: "INCREASE",
  //         payload: {
  //           id: found.id,
  //           size: found.size,
  //           amount: found.cartAmount,
  //         },
  //       });
  //     } else {
  //       dispatch({
  //         type: "ADD_TO_CART",
  //         payload: {
  //           item: { ...item, size: itemsSize },
  //         },
  //       });
  //     }
  //   } else {
  //     setProceedError(true);
  //   }
  // };
  // const deleteItemHandler = () => {
  //   dispatch(deleteItem(item._id, history));
  // };
  return <></>;
};

const ItemDetailsPageComponent = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
  min-height: 70vh;
  margin: 1rem 0;
  h1 {
    margin-left: 10%;
    margin-top: 2rem;
    @media screen and (max-width: 1000px) {
      margin-left: 0rem;
      font-size: 1.5rem;
    }
  }
  .top-side {
    width: 100%;
    display: flex;
    @media screen and (max-width: 1000px) {
      flex-direction: column;
    }
    .left-side {
      width: 50%;
      display: flex;
      justify-content: flex-end;
      align-items: center;
      @media screen and (max-width: 1000px) {
        width: 100%;
        justify-content: center;
      }
      .main-image {
        height: 70vh;
        width: 60vh;
        background-size: cover;
        display: flex;
        justify-content: space-between;
        align-items: center;
        position: relative;
        @media screen and (max-width: 1000px) {
          pointer-events: none;
        }
        &:hover {
          cursor: zoom-in;
        }
        .arrows {
          font-size: 2rem;
          margin: 1.5rem;
          @media screen and (max-width: 1000px) {
            pointer-events: auto;
          }
          &:hover {
            cursor: pointer;
          }
        }
        .image-count {
          position: absolute;
          bottom: 0;
          width: 100%;
          left: 50%;
          margin-left: -50%;
          background-color: rgba(255, 255, 255, 0.6);
          border-radius: 2rem;
          font-size: 1.5rem;
          display: none;
          @media screen and (max-width: 1000px) {
            display: flex;
            justify-content: center;
            align-items: center;
            pointer-events: auto;
          }
          .dot {
            margin: 5px;
            height: 20px;
            width: 20px;
            padding: 5px;
            border-radius: 20px;
            background-color: rgba(0, 0, 0, 0.8);
            transition: 0.3s ease-in all;
          }
          .active-dot {
            width: 40px;
          }
        }
      }
      .images-show {
        height: 70vh;
        width: 15vh;
        overflow: auto;
        margin-right: 8px;
        ::-webkit-scrollbar {
          width: 5px;
        }
        ::-webkit-scrollbar-track {
          background: white;
        }
        ::-webkit-scrollbar-thumb {
          background: black;
        }
        @media screen and (max-width: 1000px) {
          display: none;
        }
      }
    }
    .right-side {
      width: 50%;
      display: flex;
      justify-content: center;
      align-items: center;
      flex-direction: column;
      position: relative;
      @media screen and (max-width: 1000px) {
        width: 100%;
      }
      .edit-button,
      .delete-button {
        color: black;
        text-transform: upperCase;
        position: absolute;
        top: 0;
        margin: 0 2rem;
        font-size: 1.5rem;
        padding: 0.5rem;
        border: 1px solid tomato;
        transition: 0.2s ease-out;
        &:hover {
          cursor: pointer;
          color: tomato;
        }
      }
      .edit-button {
        right: 0;
      }
      .info {
        display: flex;
        flex-direction: column;
        @media screen and (max-width: 1000px) {
          margin-top: 2rem;
          text-align: center;
        }
        .name {
          font-size: 1rem;
        }
        .price {
          font-size: 1.5rem;
          font-weight: bold;
          display: flex;
          p {
            padding-left: 10px;
          }
          @media screen and (max-width: 1000px) {
            justify-content: center;
          }
        }
        .error {
          color: Red;
        }
        button {
          width: 20rem;
          background-color: black;
          color: white;
          margin: 1rem 0;
          @media screen and (max-width: 1000px) {
            font-size: 1rem;
          }
          &:hover {
            background-color: rgba(0, 0, 0, 0.8);
          }
        }
      }
      .size {
        display: flex;
        align-items: flex-start;
        flex-direction: column;
        span {
          margin: 1rem 0;
          color: rgba(0, 0, 0, 0.6);
        }
        .select {
          width: 20rem;
          height: 3rem;
          margin: 1rem 0rem;
          text-transform: upperCase;
          &:hover {
            cursor: pointer;
          }
        }
      }
    }
  }
  .bottom-side {
    width: 100%;
    .accordions {
      width: 50%;
      display: flex;
      flex-direction: column;
      align-items: flex-end;
      margin-top: 1rem;
      @media screen and (max-width: 1000px) {
        width: 100%;
      }
      .accordion {
        width: 60vh;
        @media screen and (max-width: 1000px) {
          width: 100%;
        }
        span {
          text-transform: uppercase;
          color: black;
        }
        .accordion-header {
          font-weight: bold;
          text-transform: uppercase;
        }
        .shipping-details {
          display: flex;
          flex-direction: column;
        }
      }
    }
  }
`;

export default ItemDetailsPage;
