import {
  ADD_TO_FAVORITES,
  GET_LOGGED_USER,
  UPDATE_USER,
  DELETE_ADDRESS,
} from "../constants/actionTypes";

const initState = {
  user: {},
};

const authReducer = (state = initState, action) => {
  switch (action.type) {
    case GET_LOGGED_USER:
    case ADD_TO_FAVORITES:
    case UPDATE_USER:
    case DELETE_ADDRESS:
      return {
        ...state,
        user: action.payload,
      };
    default:
      return { ...state };
  }
};

export default authReducer;
