import React from "react";
//styling
import styled from "styled-components";

const Button = ({ label, variant }) => {
  return <ButtonWrapper variant={variant}>{label}</ButtonWrapper>;
};

const ButtonWrapper = styled.div`
  height: 4rem;
  width: 20rem;
  display: flex;
  align-items: center;
  justify-content: center;
  text-transform: upperCase;
  padding: 0.5rem;
  cursor: pointer;
  margin: 10px 0;
  border: ${({ variant }) => (variant === "black" ? 0 : "1px solid black")};
  background-color: ${({ variant }) =>
    variant === "black" ? "black" : "white"};
  color: ${({ variant }) => (variant === "black" ? "white" : "black")};
  transition: 0.3s ease-in all;
  &:hover {
    background-color: ${({ variant }) =>
      variant === "black" ? "white" : "black"};
    color: ${({ variant }) => (variant === "black" ? "black" : "white")};
  }
`;

export default Button;
