    @media screen and (max-width: 1000px) {
      margin-left: 0rem;
      font-size: 1.5rem;
    }
  }
  .top-side {
    width: 100%;
    display: flex;
    @media screen and (max-width: 1000px) {
      flex-direction: column;
    }
    .left-side {
      width: 50%;
      display: flex;
      justify-content: flex-end;
      align-items: center;
      @media screen and (max-width: 1000px) {
        width: 100%;
        justify-content: center;
      }
      .main-image {
        height: 70vh;
        width: 60vh;
        background-size: cover;
        display: flex;
        justify-content: space-between;
        align-items: center;
        position: relative;
        @media screen and (max-width: 1000px) {
          pointer-events: none;
        }
        &:hover {
          cursor: zoom-in;
        }
        .arrows {
          font-size: 2rem;
          margin: 1.5rem;
          @media screen and (max-width: 1000px) {
            pointer-events: auto;
          }
          &:hover {
            cursor: pointer;
          }
        }
        .image-count {
          position: absolute;
          bottom: 0;
          width: 100%;
          left: 50%;
          margin-left: -50%;
          background-color: rgba(255, 255, 255, 0.6);
          border-radius: 2rem;
          font-size: 1.5rem;
          display: none;
          @media screen and (max-width: 1000px) {
            display: flex;
            justify-content: center;
            align-items: center;
            pointer-events: auto;
          }
          .dot {
            margin: 5px;
            height: 20px;
            width: 20px;
            padding: 5px;
            border-radius: 20px;
            background-color: rgba(0, 0, 0, 0.8);
            transition: 0.3s ease-in all;
          }
          .active-dot {
            width: 40px;
          }
        }
      }
      .images-show {
        height: 70vh;
        width: 15vh;
        overflow: auto;
        ::-webkit-scrollbar {
          width: 5px;
        }
        ::-webkit-scrollbar-track {
          background: white;
        }
        ::-webkit-scrollbar-thumb {
          background: black;
        }
        @media screen and (max-width: 1000px) {
          display: none;
        }
      }
    }
    .right-side {
      width: 50%;
      display: flex;
      justify-content: center;
      align-items: center;
      flex-direction: column;
      position: relative;
      .edit-item {
        display: flex;
        flex-direction: column;
        .input,
        .select {
          width: 15rem;
          margin: 0.5rem 0;
        }
      }
      @media screen and (max-width: 1000px) {
        width: 100%;
      }
      .edit-button {
        position: absolute;
        top: 0;
        right: 0;
        margin: 0 2rem;
        font-size: 1.5rem;
        &:hover {
          cursor: pointer;
        }
      }
      .info {
        display: flex;
        flex-direction: column;
        @media screen and (max-width: 1000px) {
          margin-top: 2rem;
          text-align: center;
        }
        .name {
          font-size: 1rem;
        }
        .price {
          font-size: 1.5rem;
          font-weight: bold;
          display: flex;
          p {
            padding-left: 10px;
          }
          @media screen and (max-width: 1000px) {
            justify-content: center;
          }
        }
        .error {
          color: Red;
        }
        button {
          width: 20rem;
          background-color: black;
          color: white;
          margin: 1rem 0;
          @media screen and (max-width: 1000px) {
            font-size: 1rem;
          }
          &:hover {
            background-color: rgba(0, 0, 0, 0.8);
          }
        }
      }
      .size {
        display: flex;
        align-items: flex-start;
        flex-direction: column;
        span {
          margin: 1rem 0;
          color: rgba(0, 0, 0, 0.6);
        }
        .select {
          width: 20rem;
          height: 3rem;
          margin: 1rem 0rem;
          text-transform: upperCase;
          &:hover {
            cursor: pointer;
          }
        }
      }
    }
  }
  .bottom-side {
    width: 100%;
    .accordions {
      width: 50%;
      display: flex;
      flex-direction: column;
      align-items: flex-end;
      margin-top: 1rem;
      @media screen and (max-width: 1000px) {
        width: 100%;
      }
      .accordion {
        width: 60vh;
        @media screen and (max-width: 1000px) {
          width: 100%;
        }
        span {
          text-transform: uppercase;
          color: black;
        }
        .accordion-header {
          font-weight: bold;
          text-transform: uppercase;
        }
        .shipping-details {
          display: flex;
          flex-direction: column;
        }
      }
    }
  }
`;

export default ItemDetailsPage;
