import React, { useEffect, useState } from "react";
//styling
import styled from "styled-components";
//components
import Button from "../components/Button";
//material ui
import Breadcrumbs from "@material-ui/core/Breadcrumbs";
//react router
import { Link, useHistory } from "react-router-dom";
//components
import Order from "../components/Order";
//redux
import { useDispatch, useSelector } from "react-redux";
//actions
import { loadUsersOrders } from "../actions/ordersAction";

const OrderDetails = ({ user, orderDetails }) => {
  const [order, setOrder] = useState([]);
  const dispatch = useDispatch();
  const history = useHistory();
  useEffect(() => {
    if (user) {
      dispatch(loadUsersOrders(user.id));
    }
  }, [dispatch, user]);
  const { userOrders } = useSelector((state) => state.orders);
  const orderDetailsHandler = (id, order) => {
    history.push(`/customer/account/orders/${id}`);
    setOrder(order);
    window.scrollTo(0, 0);
  };
  return (
    <OrdersComponent>
      {userOrders.length > 0 ? (
        <>
          <Breadcrumbs aria-label="breadcrumb">
            <span>My account</span>
            <Link to="/customer/account/orders" className="link">
              My orders
            </Link>
          </Breadcrumbs>
          {!orderDetails ? (
            <>
              {userOrders.map((order) => (
                <div className="order" key={order.id}>
                  <div className="order-left">
                    <div className="price-info">
                      <span>Nr:{order.id}</span>
                      <span>
                        <b>
                          {(order.cartPrice + order.deliveryPrice).toFixed(2)}{" "}
                          GBP
                        </b>
                      </span>
                    </div>
                    <div className="items-image">
                      {order.items.map((item) => (
                        <img
                          src={item.images[0].img}
                          alt={item.name}
                          key={item.id}
                          onClick={() => orderDetailsHandler(order.id, order)}
                        />
                      ))}
                    </div>
                  </div>
                  <div className="order-right">
                    <div className="time-info">
                      <span>
                        {order.date} {order.time}
                      </span>
                      <span>{order.status}</span>
                    </div>
                    <div className="details">
                      <span
                        className="details-button"
                        onClick={() => orderDetailsHandler(order.id, order)}
                      >
                        Details
                      </span>
                    </div>
                  </div>
                </div>
              ))}
            </>
          ) : (
            <div className="specific-order">
              <Breadcrumbs aria-label="breadcrumb">
                <span>My account</span>
                <Link to="/customer/account/orders" className="link">
                  My orders
                </Link>
                <span>{order.id}</span>
              </Breadcrumbs>
              <Order />
            </div>
          )}
        </>
      ) : (
        <div className="no-orders">
          <h2>You didnt order anything</h2>
          <span>Go and explore</span>
          <div className="buttons">
            <Link to="/woman">
              <Button label="Women" />
            </Link>
            <Link to="/man">
              <Button label="Men" />
            </Link>
          </div>
        </div>
      )}
    </OrdersComponent>
  );
};

const OrdersComponent = styled.div`
  margin-left: 2rem;
  @media screen and (max-width: 1000px) {
    margin-left: 0;
  }
  .order {
    display: flex;
    justify-content: space-between;
    font-size: 1.5rem;
    border-bottom: 1px solid rgba(0, 0, 0, 0.2);
    padding: 1rem;

    .order-left {
      display: flex;
      flex-direction: column;
      justify-content: space-between;
      .price-info {
        display: flex;
        flex-direction: column;
        font-size: 1rem;
      }
      .items-image {
        display: flex;
        flex-wrap: wrap;
        img {
          margin: 0.5rem 0.5rem 0.5rem 0;
          height: 10rem;
          width: 8rem;
        }
      }
    }
    .order-right {
      display: flex;
      flex-direction: column;
      justify-content: space-between;
      .time-info {
        display: flex;
        flex-direction: column;
        font-size: 1rem;
        span {
          align-self: flex-end;
        }
      }
      .details {
        align-self: flex-end;
        span {
          padding: 0.6rem;
          font-size: 1rem;
          font-weight: bold;
          text-transform: upperCase;
          &:hover {
            background-color: rgba(0, 0, 0, 0.2);
            cursor: pointer;
          }
        }
      }
    }
  }
`;

export default OrderDetails;
