import React from "react";
//styling
import styled from "styled-components";
//material ui
import Input from "../components/Input";
//icons
import CloseIcon from "@material-ui/icons/Close";
//data
import { addressInputs } from "../descriptions/inputs";
//components
import Button from "../components/Button";

const AnonymousAddressModal = ({
  addressData,
  handleForm,
  setAnonymousModal,
  anonymousModal,
  setChosenAddress,
  areInputsEmpty,
  setAddressData,
}) => {
  //setting address
  const anonymousAddressHandler = () => {
    if (areInputsEmpty()) {
      setAnonymousModal(!anonymousModal);
      setChosenAddress(true);
    }
  };
  return (
    <AddressComponent style={{ display: anonymousModal ? "block" : "none" }}>
      <div className="anonymous-address">
        <div className="header">
          <span>Add</span>
          <CloseIcon
            className="close-modal"
            onClick={() => setAnonymousModal(!anonymousModal)}
          />
        </div>
        <div className="inputs">
          {addressInputs.map((input) => (
            <Input
              label={input.label}
              name={input.name}
              value={addressData[input.name]}
              type={input.type}
              handleChange={(e) => handleForm(e, setAddressData, addressData)}
              required={input.required}
            />
          ))}
        </div>
        <Button
          label="save"
          variant="black"
          onClick={() => anonymousAddressHandler()}
        >
          Save
        </Button>
      </div>
    </AddressComponent>
  );
};

const AddressComponent = styled.div`
  position: absolute;
  z-index: 5;
  background-color: rgba(0, 0, 0, 0.4);
  left: 0;
  top: 0;
  height: 100%;
  width: 100%;
  .anonymous-address {
    background-color: white;
    margin-left: 40%;
    margin-top: 10%;
    width: 25%;
    justify-content: center;
    padding: 1rem;
    @media screen and (max-width: 1000px) {
      width: 100%;
      margin: 0;
    }
    .header {
      display: flex;
      justify-content: space-between;
      width: 100%;
      padding: 1rem;
      span {
        &:hover {
          cursor: pointer;
        }
      }
    }
    .inputs {
      display: flex;
      flex-direction: column;
      align-items: center;
      width: 100%;
      padding: 1rem;
      .input {
        width: 100%;
      }
      .two-inputs {
        width: 100%;
        display: flex;
      }
      .postal {
        margin-right: 10px;
      }
    }
    .button-black {
      width: 100%;
      padding: 1rem;
      font-size: 1.5rem;
      text-align: center;
      &:hover {
        cursor: pointer;
      }
    }
  }
`;

export default AnonymousAddressModal;
