import { LOGIN } from "../constants/actionTypes";
import * as api from "../api/index.js";

export const signin = (formData, router) => async (dispatch) => {
  try {
    const { data } = await api.signIn(formData);
    dispatch({ type: LOGIN, data });
    router.push("/");
  } catch (error) {
    console.log(error);
  }
};
