import axios from "axios";
import * as api from "../api";
import {
  CREATE,
  FETCH_ITEM,
  START_LOADING,
  END_LOADING,
  FETCH_ALL,
  FETCH_BY_ITEM,
  FETCH_BY_SEARCH,
  FETCH_ON_SALE,
  UPDATE_ITEM,
  DELETE,
} from "../constants/actionTypes";

//action creator
export const loadItems =
  (gender, category, item, sortOrder, page) => async (dispatch) => {
    dispatch({
      type: "LOADING_DETAIL",
    });
    //fetch axios
    const itemsData = await axios.get(
      api.itemsUrl(gender, category, item, sortOrder, page)
    );
    dispatch({
      type: "FETCH_ITEMS",
      payload: {
        items: itemsData.data,
      },
    });
  };
export const getItems = (gender) => async (dispatch) => {
  try {
    dispatch({ type: START_LOADING });
    const { data } = await api.getItems(gender);
    dispatch({ type: FETCH_ALL, payload: data });
    dispatch({ type: END_LOADING });
  } catch (error) {
    console.log(error);
  }
};
export const getItemsByItem = (gender, item, order) => async (dispatch) => {
  try {
    dispatch({ type: START_LOADING });
    const {
      data: { data, currentPage, numberOfPages },
    } = await api.getItemsByItem(gender, item, order);
    dispatch({
      type: FETCH_BY_ITEM,
      payload: { data, currentPage, numberOfPages },
    });
    dispatch({ type: END_LOADING });
  } catch (error) {
    console.log(error);
  }
};
export const getItemsBySearch = (gender, search) => async (dispatch) => {
  try {
    dispatch({ type: START_LOADING });
    const { data } = await api.getItemsBySearch(gender, search);
    dispatch({ type: FETCH_BY_SEARCH, payload: data });
    dispatch({ type: END_LOADING });
  } catch (error) {
    console.log(error);
  }
};
export const getItemsOnSale = (gender, item) => async (dispatch) => {
  try {
    dispatch({ type: START_LOADING });
    const { data } = await api.getItemsOnSale(gender, item);
    dispatch({ type: FETCH_ON_SALE, payload: data });
    dispatch({ type: END_LOADING });
  } catch (error) {
    console.log(error);
  }
};
export const getItem = (id) => async (dispatch) => {
  try {
    dispatch({ type: START_LOADING });
    const { data } = await api.getItem(id);
    dispatch({ type: FETCH_ITEM, payload: { item: data } });
    dispatch({ type: END_LOADING });
  } catch (error) {
    console.log(error);
  }
};

export const createItem = (item, history) => async (dispatch) => {
  try {
    const { data } = await api.createItem(item);
    dispatch({ type: CREATE, payload: data });
    history.push(`/items/${data._id}`);
  } catch (error) {
    console.log(error);
  }
};

export const updateItem = (id, item, history) => async (dispatch) => {
  try {
    const { data } = await api.updateItem(id, item);
    dispatch({ type: UPDATE_ITEM, payload: data });
    history.push(`/items/${data._id}`);
  } catch (error) {
    console.log(error);
  }
};
export const deleteItem = (id, history) => async (dispatch) => {
  try {
    await await api.deleteItem(id);

    dispatch({ type: DELETE, payload: id });
    history.goBack();
  } catch (error) {
    console.log(error);
  }
};

export const loadFavorites = (gender, id) => async (dispatch) => {
  //fetch data
  const itemData = await axios.get(api.specificItemUrl(gender, id));
  dispatch({
    type: "FETCH_FAVORITE",
    payload: {
      favorite: itemData.data,
    },
  });
};

export const loadQuestion = (question) => async (dispatch) => {
  dispatch({
    type: "ASK",
    payload: {
      question: question,
    },
  });
};
export const loadSearched =
  (gender, question, sortOrder, limit) => async (dispatch) => {
    //fetch data
    const answersData = await axios.get(
      api.questionUrl(gender, question, sortOrder, limit)
    );
    dispatch({
      type: "FETCH_ANSWER",
      payload: {
        answer: answersData.data,
      },
    });
  };
export const loadAllSearched = (gender, question) => async (dispatch) => {
  //fetch data
  const answersData = await axios.get(api.fullQuestionLength(gender, question));
  dispatch({
    type: "FETCH_ALL_ANSWERS",
    payload: {
      answer: answersData.data,
    },
  });
};

export const loadSale =
  (gender, item, sortOrder, limit) => async (dispatch) => {
    //fetch data
    const saleData = await axios.get(
      api.discountUrl(gender, item, sortOrder, limit)
    );
    dispatch({
      type: "FETCH_SALE",
      payload: {
        sale: saleData.data,
      },
    });
  };

export const changeLimit = (number) => async (dispatch) => {
  dispatch({
    type: "CHANGE_LIMIT",
    payload: {
      number: number,
    },
  });
};

export const changeSaleLimit = (number) => async (dispatch) => {
  dispatch({
    type: "CHANGE_SALE_LIMIT",
    payload: {
      number: number,
    },
  });
};
export const changeSearchLimit = (number) => async (dispatch) => {
  dispatch({
    type: "CHANGE_SEARCH_LIMIT",
    payload: {
      number: number,
    },
  });
};
