import React, { useEffect, useState } from "react";
//redux
import { useDispatch, useSelector } from "react-redux";
//actions
import { changeLimit, getItemsByItem } from "../actions/itemsAction";
import { loginAction } from "../actions/loginAction";
//styling
import styled from "styled-components";
//router
import { useLocation } from "react-router-dom";
//icons
import ViewComfyIcon from "@material-ui/icons/ViewComfy";
import ViewColumnIcon from "@material-ui/icons/ViewColumn";
//components
import Card from "../components/Card";
import SortPrice from "../components/SortPrice";
import WomanLinksComponent from "../components/WomanLinksComponent";
import ManLinksComponent from "../components/ManLinksComponent";
import ShowLoading from "../components/ShowLoading";
//scroll bottom
import { BottomScrollListener } from "react-bottom-scroll-listener";

const ClothesPreviewPage = ({ gender }) => {
  //state
  const [smallView, setSmallView] = useState(false);
  const [sort, setSort] = useState("");
  const location = useLocation();
  const item = location.pathname.split("/")[3];
  const subItem = location.pathname.split("/")[4];
  //card height and width
  const [cardLgWidth, setCardLgWidth] = useState("24%");
  const [cardLgHeight, setCardLgHeight] = useState("32rem");
  const [cardSmWidth, setCardSmWidth] = useState("48%");
  const [cardSmHeight, setCardSmHeight] = useState("20rem");
  //dispatch data
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(loginAction(localStorage.getItem("userId")));
    dispatch(getItemsByItem(gender, subItem ? subItem : item));
  }, [dispatch, gender, subItem, item]);
  //get data back
  const { items, isLoading } = useSelector((state) => state.item);
  //handlers
  const handleSort = (event) => {
    setSort(event.target.value);
  };
  const handleLimit = () => {
    dispatch(changeLimit(20));
  };
  return (
    <>
      <ClothesPreviewPageComponent>
        <BottomScrollListener onBottom={handleLimit} offset={700} />
        <div className="left-side">
          {gender === "woman" && <WomanLinksComponent gender={gender} />}
          {gender === "man" && <ManLinksComponent gender={gender} />}
        </div>
        <ShowLoading isLoading={isLoading}>
          <div className="right-side">
            <div className="items-name">
              <span>{subItem ? subItem : item}</span>
            </div>
            <div className="options-component">
              <div className="sort">
                <SortPrice sort={sort} handleSort={handleSort} />
              </div>
              <div className="display-info">
                {items.length} {items.length === 1 ? "Product" : "Products"}
                <ViewComfyIcon
                  className={smallView ? "view-icon" : "view-icon active-icon"}
                  onClick={() => {
                    setCardSmWidth("48%");
                    setCardLgWidth("24%");
                    setCardLgHeight("30rem");
                    setCardSmHeight("20rem");
                    setSmallView(false);
                  }}
                />
                <ViewColumnIcon
                  className={smallView ? "view-icon active-icon" : "view-icon"}
                  onClick={() => {
                    setCardLgWidth("32%");
                    setCardSmWidth("90%");
                    setCardLgHeight("40rem");
                    setCardSmHeight("24rem");
                    setSmallView(true);
                  }}
                />
              </div>
            </div>
            <div className="items-display">
              {items?.map((item) => (
                <Card
                  key={item.id}
                  item={item}
                  id={item.id}
                  lgHeight={cardLgHeight}
                  smHeight={cardSmHeight}
                  lgWidth={cardLgWidth}
                  smWidth={cardSmWidth}
                  lgMargin={"1.5rem 0.2rem"}
                  smMargin={"1rem 0.2rem"}
                  gender={gender}
                />
              ))}
            </div>
          </div>
        </ShowLoading>
      </ClothesPreviewPageComponent>
    </>
  );
};

const ClothesPreviewPageComponent = styled.div`
  width: 100%;
  min-height: 100vh;
  display: flex;
  margin-top: 2rem;
  .left-side {
    position: fixed;
    display: flex;
    align-items: center;
    flex-direction: column;
    margin-left: 2rem;
    @media screen and (max-width: 1000px) {
      display: none;
    }
  }
  .right-side {
    width: 80%;
    margin-left: 23rem;
    @media screen and (max-width: 1000px) {
      margin: 0;
      width: 100%;
    }
    .items-name {
      font-weight: Bold;
      letter-spacing: 2px;
      @media screen and (max-width: 1000px) {
        text-align: Center;
      }
    }
    .options-component {
      width: 100%;
      height: 4rem;
      display: flex;
      justify-content: space-between;
      align-items: Center;
      .sort {
        .sort-label {
          text-transform: upperCase;
          @media screen and (max-width: 1000px) {
            font-size: 0.6rem;
          }
        }
        .sort-select {
          width: 10rem;
          @media screen and (max-width: 1000px) {
            width: 6rem;
            margin-left: 10px;
          }
        }
      }
      .display-info {
        padding: 0rem 2rem;

        .view-icon {
          margin: 0rem 1rem;
          font-size: 1.5rem;
          @media screen and (max-width: 1000px) {
            margin: 0rem 0.5rem;
          }
        }
        .active-icon {
          background-color: rgba(0, 0, 0, 0.2);
          border-radius: 1rem;
        }
      }
    }
    .items-display {
      display: flex;
      flex-wrap: wrap;
      @media screen and (max-width: 1000px) {
        justify-content: center;
      }
    }
  }
`;

export default ClothesPreviewPage;
