import React, { useState } from "react";
//styling
import styled from "styled-components";
//components
import Input from "../components/Input";
import Button from "../components/Button";
//data
import { accountInputs, passwordInputs } from "../descriptions/inputs";

const AccountInfo = () => {
  const [deleteInfo, setDeleteInfo] = useState(false);
  //users State
  const [userData, setUserData] = useState({
    name: "",
    surname: "",
    email: "",
    phone: "",
  });
  const [passwordData, setPasswordData] = useState({
    password: "",
    newPassword: "",
    confirmPassword: "",
  });
  const [passwordErrorMsg, setPasswordErrorMsg] = useState("");
  const [accountErrorMsg, setAccountErrorMsg] = useState("");
  //handlers
  const userAccountHandler = () => {};
  const passwordChangeHandler = () => {};
  const deleteAccountHandler = () => {};
  const handleForm = (e, setData, data) =>
    setData({ ...data, [e.target.name]: e.target.value });
  return (
    <InfoComponent>
      <div className="info">
        <span>My account</span>
        <div className="line"></div>
        <span>{accountErrorMsg}</span>
        {accountInputs.map((input) => (
          <span>
            <Input
              label={input.label}
              name={input.name}
              value={userData[input.name]}
              type={input.type}
              handleChange={(e) => handleForm(e, setUserData, userData)}
              required={input.required}
            />
          </span>
        ))}
        <Button
          onClick={() => userAccountHandler()}
          label="Save changes"
          variant="black"
        />
        <div className="line"></div>
        <div className="password-change">
          <span>Password change</span>
          <span>{passwordErrorMsg}</span>
          {passwordInputs.map((input) => (
            <Input
              label={input.label}
              name={input.name}
              value={passwordData[input.name]}
              type={input.type}
              handleChange={(e) => handleForm(e, setPasswordData, passwordData)}
              required={input.required}
            />
          ))}
          <Button
            onClick={() => passwordChangeHandler()}
            label="Change password"
            variant="black"
          />
        </div>
        <div className="line"></div>
        <div className="delete-acc">
          <span onClick={() => setDeleteInfo(true)}>Delete account</span>
          <div
            style={{ display: deleteInfo ? "block" : "none" }}
            className="delete-confirmation"
          >
            <span>Are you sure you want to delete account?</span>
            <div className="buttons">
              <Button onClick={() => deleteAccountHandler()} label="Yes" />
              <Button
                onClick={() => setDeleteInfo(false)}
                label="No"
                variant="black"
              />
            </div>
          </div>
        </div>
        <div className="line"></div>
      </div>
    </InfoComponent>
  );
};

const InfoComponent = styled.div`
  display: flex;
  flex-direction: column;
  margin-left: 2rem;
  @media screen and (max-width: 1000px) {
    margin: 0rem;
  }
  .info {
    display: flex;
    flex-direction: column;
    text-align: left;
    .input {
      width: 15rem;
    }
    .password-change {
      display: flex;
      flex-direction: column;
    }
    .delete-acc {
      .delete-confirmation {
        display: flex;
        flex-direction: column;
        margin: 1rem 0rem;
      }
    }
  }
`;

export default AccountInfo;
