import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
//styling
import styled from "styled-components";
import { getFavorites } from "../actions/itemsAction";
//components
import Card from "../components/Card";
import ShowLoading from "../components/ShowLoading";
import Pagination from "../components/Pagination";

const FavoritesPage = () => {
  const { user, isLoading, numberOfPages } = useSelector((state) => state.user);
  const [page, setPage] = useState(1);
  useEffect(() => {
    getFavorites(user._id, page);
  }, [user._id, page]);
  return (
    <FavoritesPageComponent>
      <ShowLoading isLoading={isLoading}>
        <div className="header">
          <h2>Favorites</h2>
          {user && user.favorites && (
            <span className="products-amount">
              {user?.favorites?.length}{" "}
              {user.favorites.length === 1 ? "item" : "items"}
            </span>
          )}
        </div>
        {user && (
          <div className="items-display">
            {user?.favorites?.length > 0
              ? user?.favorites?.map((item) => (
                  <Card
                    key={item._id}
                    lgHeight={"30rem"}
                    smHeight={"20rem"}
                    smWidth={"48%"}
                    lgWidth={"24%"}
                    lgMargin={"1rem 0.3rem"}
                    smMargin={"0.5rem 0.1px"}
                    id={item._id}
                    gender={item.gender}
                    category={item.category}
                    item={item}
                  />
                ))
              : "Add item to favorites"}
          </div>
        )}
        <Pagination
          page={page}
          numberOfPages={numberOfPages}
          setPage={setPage}
        />
      </ShowLoading>
    </FavoritesPageComponent>
  );
};

const FavoritesPageComponent = styled.div`
  min-height: 90vh;
  display: flex;
  flex-direction: column;
  margin-top: 2rem;
  align-items: center;
  .header {
    width: 80%;
    position: relative;
    display: flex;
    text-align: Center;
    justify-content: center;
    @media screen and (max-width: 1000px) {
      width: 100%;
    }
    h2 {
      @media screen and (max-width: 1000px) {
        font-size: 1.5rem;
      }
    }
    .products-amount {
      position: absolute;
      right: 0;
      top: 0;
      margin: 0rem 0.5rem;
    }
  }
  .items-display {
    margin-top: 2rem;
    width: 80%;
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
    @media screen and (max-width: 1000px) {
      width: 100%;
    }
  }
`;

export default FavoritesPage;
