import React from "react";
//icons
import DeleteOutlineIcon from "@material-ui/icons/DeleteOutline";
//redux
import { useDispatch } from "react-redux";
//router
import { Link } from "react-router-dom";

const CartItem = ({ item }) => {
  console.log(item);
  const {
    gender,
    _id,
    name,
    size,
    cartAmount,
    images,
    price,
    discount,
    beforeDiscount,
  } = item;
  const dispatch = useDispatch();
  const amountHandler = (type, id, amount, size) => {
    dispatch({
      type: type,
      payload: {
        id: id,
        amount: amount,
        size: size,
      },
    });
  };
  const removeHandler = (id, size) => {
    dispatch({
      type: "REMOVE",
      payload: {
        id: id,
        size: size,
      },
    });
  };
  return (
    <div className="item">
      <div className="item-info">
        <Link to={`/${gender}/${_id}`}>
          <div className="image">
            <img src={images[0].url} alt={name} />
          </div>
        </Link>
        <div className="info">
          <div className="info-details">
            <h2>{name}</h2>
            <span>
              Size: <b>{size}</b>
            </span>
          </div>
          <div className="amount">
            <b
              className="amount-change"
              onClick={() => amountHandler("DECREASE", _id, cartAmount, size)}
            >
              -
            </b>{" "}
            {cartAmount}{" "}
            <b
              className="amount-change"
              onClick={() => amountHandler("INCREASE", _id, cartAmount, size)}
            >
              +
            </b>
          </div>
        </div>
      </div>
      <div className="item-remove">
        <div className="remove">
          <DeleteOutlineIcon onClick={() => removeHandler(_id, size)} />
        </div>
        <div className="price">
          <span
            style={{
              color: discount === true ? "red" : "black",
            }}
          >
            {(cartAmount * price).toFixed(2)} GBP
          </span>
          <span
            style={{
              display: discount === true ? "flex" : "none",
              textDecoration: "line-through",
            }}
          >
            {(cartAmount * beforeDiscount).toFixed(2)} GBP
          </span>
        </div>
      </div>
    </div>
  );
};

export default CartItem;
