import React, { useState, useEffect } from "react";
//react router
import { useLocation } from "react-router";
import { Link, useHistory } from "react-router-dom";
//redux
import { useDispatch, useSelector } from "react-redux";
//actions
import { loadUsersOrders } from "../actions/ordersAction";
//styling
import styled from "styled-components";
//material ui
import TextField from "@material-ui/core/TextField";
import Checkbox from "@material-ui/core/Checkbox";
import Breadcrumbs from "@material-ui/core/Breadcrumbs";
//icons
import ShoppingBasketIcon from "@material-ui/icons/ShoppingBasket";
import AccountBoxIcon from "@material-ui/icons/AccountBox";
import MeetingRoomIcon from "@material-ui/icons/MeetingRoom";
import HomeIcon from "@material-ui/icons/Home";
//axios
import axios from "axios";
//encrypting password
import sha512 from "crypto-js/sha512";
import Base64 from "crypto-js/enc-base64";
//components
import Order from "../components/Order";

const AccountPage = () => {
  const [deleteInfo, setDeleteInfo] = useState(false);
  //users State
  const [userData, setUserData] = useState({
    name: "",
    surname: "",
    email: "",
    phone: "",
  });
  const [addressData, setAddressData] = useState({
    street: "",
    city: "",
    postalCode: "",
    houseNr: "",
  });
  const [addressMsg, setAddressMsg] = useState("");
  const [newAddressCheckbox, setNewAddressCheckbox] = useState(false);
  const [addressId, setAddressId] = useState(1);
  const [order, setOrder] = useState([]);
  //account state
  const [newPassword, setNewPassword] = useState("");
  const [oldPassword, setOldPassword] = useState("");
  const [confirmNewPassword, setConfirmNewPassword] = useState("");
  const [passwordErrorMsg, setPasswordErrorMsg] = useState("");
  const [accountErrorMsg, setAccountErrorMsg] = useState("");
  //use
  const location = useLocation();
  const history = useHistory();
  const dispatch = useDispatch();
  //pathName
  const pathName = location.pathname.split("/")[3];
  const orderDetails = location.pathname.split("/")[4];
  const [action, setAction] = useState("change");
  const { user } = useSelector((state) => state.user);

  useEffect(() => {
    if (user) {
      dispatch(loadUsersOrders(user.id));
    }
  }, [dispatch, user]);
  const { userOrders } = useSelector((state) => state.orders);

  //handlers
  const userAccountHandler = () => {};

  const passwordChangeHandler = () => {};
  const deleteAccountHandler = () => {};
  //edits address
  const addressHandler = () => {};
  //adds address
  const newAddressHandler = () => {
    setNewAddressCheckbox(true);
    setAddressId(0);
    setAction("add");
    setUserData({
      email: "",
      phone: "",
    });
    setAddressData({
      street: "",
      city: "",
      postalCode: "",
      houseNr: "",
    });
  };
  useEffect(() => {
    if (user) {
      if (user.addresses.length === 0) {
        newAddressHandler();
      }
    }
  }, [user]);
  const addAddress = () => {};
  const checkboxHandler = (e) => {
    setNewAddressCheckbox(e.target.checked);
    if (newAddressCheckbox === false) {
      newAddressHandler();
    }
  };
  const LogOutHandler = () => {
    localStorage.removeItem("userId");
    history.push("/customer/account/login");
  };
  //deletes address
  const deleteAddressHandler = (id) => {};
  const chooseAddressHandler = (city, houseNr, street, postalCode, id) => {
    setAddressData({
      street: street,
      city: city,
      postalCode: postalCode,
      houseNr: houseNr,
    });
    setAction("change");
    setAddressId(id);
    setNewAddressCheckbox(false);
  };
  const orderDetailsHandler = (id, order) => {
    history.push(`/customer/account/orders/${id}`);
    setOrder(order);
    window.scrollTo(0, 0);
  };
  return (
    <>
      {user && (
        <LoggedInComponent>
          <div className="left-side">
            <ul>
              <Link to="/customer/account/orders" className="link">
                <li className={pathName === "orders" ? "active" : ""}>
                  <ShoppingBasketIcon /> Orders
                </li>
              </Link>
              <Link to="/customer/account/address" className="link">
                <li className={pathName === "address" ? "active" : ""}>
                  <HomeIcon /> Address data
                </li>
              </Link>

              <Link to="/customer/account/info" className="link">
                <li className={pathName === "info" ? "active" : ""}>
                  <AccountBoxIcon /> Account info
                </li>
              </Link>
              <li className="log-out" onClick={() => LogOutHandler()}>
                <MeetingRoomIcon /> Log out
              </li>
            </ul>
          </div>
          {user && (
            <div className="right-side">
              {pathName === "info" && (
                <div className="infoComponent">
                  <div className="info">
                    <span>My account</span>
                    <div className="line"></div>
                    <span>{accountErrorMsg}</span>

                    <span>
                      <TextField
                        label="Name"
                        value={usersName}
                        className="input"
                        required
                        onChange={(e) => setUsersName(e.target.value)}
                      />
                    </span>
                    <span>
                      <TextField
                        label="Surname"
                        value={usersSurname}
                        required
                        className="input"
                        onChange={(e) => setUsersSurname(e.target.value)}
                      />
                    </span>
                    <span>
                      <TextField
                        label="Email"
                        type="email"
                        required
                        value={usersEmail}
                        className="input"
                        onChange={(e) => setUsersEmail(e.target.value)}
                      />
                    </span>
                    <button onClick={() => userAccountHandler()}>
                      Save Changes
                    </button>
                    <div className="line"></div>
                    <div className="password-change">
                      <span>Password change</span>
                      <span>{passwordErrorMsg}</span>
                      <TextField
                        label="Your Password"
                        type="password"
                        value={oldPassword}
                        required
                        className="input"
                        onChange={(e) => setOldPassword(e.target.value)}
                      />
                      <TextField
                        label="New password"
                        type="password"
                        value={newPassword}
                        required
                        className="input"
                        onChange={(e) => setNewPassword(e.target.value)}
                      />
                      <TextField
                        label="Confirm password"
                        type="email"
                        value={confirmNewPassword}
                        required
                        className="input"
                        onChange={(e) => setConfirmNewPassword(e.target.value)}
                      />
                      <button onClick={() => passwordChangeHandler()}>
                        Change
                      </button>
                    </div>
                    <div className="line"></div>
                    <div className="delete-acc">
                      <span onClick={() => setDeleteInfo(true)}>
                        Delete account
                      </span>
                      <div
                        style={{ display: deleteInfo ? "block" : "none" }}
                        className="delete-confirmation"
                      >
                        <span>Are you sure you want to delete account?</span>
                        <div className="buttons">
                          <button onClick={() => deleteAccountHandler()}>
                            Yes
                          </button>
                          <button onClick={() => setDeleteInfo(false)}>
                            No
                          </button>
                        </div>
                      </div>
                    </div>
                    <div className="line"></div>
                  </div>
                </div>
              )}
              {pathName === "orders" && !orderDetails && (
                <div className="orders">
                  {userOrders.length > 0 ? (
                    <>
                      <Breadcrumbs aria-label="breadcrumb">
                        <span>My account</span>
                        <Link to="/customer/account/orders" className="link">
                          My orders
                        </Link>
                      </Breadcrumbs>
                      {userOrders.map((order) => (
                        <div className="order" key={order.id}>
                          <div className="order-left">
                            <div className="price-info">
                              <span>Nr:{order.id}</span>
                              <span>
                                <b>
                                  {(
                                    order.cartPrice + order.deliveryPrice
                                  ).toFixed(2)}{" "}
                                  GBP
                                </b>
                              </span>
                            </div>
                            <div className="items-image">
                              {order.items.map((item) => (
                                <img
                                  src={item.images[0].img}
                                  alt={item.name}
                                  key={item.id}
                                  onClick={() =>
                                    orderDetailsHandler(order.id, order)
                                  }
                                />
                              ))}
                            </div>
                          </div>
                          <div className="order-right">
                            <div className="time-info">
                              <span>
                                {order.date} {order.time}
                              </span>
                              <span>{order.status}</span>
                            </div>
                            <div className="details">
                              <span
                                className="details-button"
                                onClick={() =>
                                  orderDetailsHandler(order.id, order)
                                }
                              >
                                Details
                              </span>
                            </div>
                          </div>
                        </div>
                      ))}
                    </>
                  ) : (
                    <div className="no-orders">
                      <h2>You didnt order anything</h2>
                      <span>Go and explore</span>
                      <div className="buttons">
                        <Link to="/woman">
                          <button>Women</button>
                        </Link>
                        <Link to="/man">
                          <button>Men</button>
                        </Link>
                      </div>
                    </div>
                  )}
                </div>
              )}
              {orderDetails && (
                <div className="specific-order">
                  <Breadcrumbs aria-label="breadcrumb">
                    <span>My account</span>
                    <Link to="/customer/account/orders" className="link">
                      My orders
                    </Link>
                    <span>{order.id}</span>
                  </Breadcrumbs>
                  <Order />
                </div>
              )}
              {pathName === "address" && (
                <div className="addressComponent">
                  <h2>My account</h2>
                  <div className="line"></div>
                  <h2>Addresses</h2>
                  <div className="choose-address">
                    {user.addresses.map((info, index) => (
                      <div
                        key={info.id}
                        className={
                          index + 1 === addressId
                            ? "single-address active-address"
                            : "single-address"
                        }
                        onClick={() =>
                          chooseAddressHandler(
                            info.name,
                            info.surname,
                            info.city,
                            info.phone,
                            info.houseNr,
                            info.street,
                            info.postalCode,
                            info.id
                          )
                        }
                      >
                        <div className="checkbox"></div>
                        <div className="address-info">
                          <span>
                            {info.name} {info.surname}
                          </span>
                          <span>
                            {info.street} {info.houseNr}
                          </span>
                          <span>
                            {info.postalCode} {info.city}
                          </span>
                          <span> Tel. {info.phone}</span>
                          <p onClick={() => deleteAddressHandler(info.id)}>
                            Delete address
                          </p>
                        </div>
                      </div>
                    ))}
                    <div className="address">
                      <Checkbox
                        checked={newAddressCheckbox}
                        onChange={checkboxHandler}
                        color="primary"
                      />
                      <span
                        style={{ fontSize: "0.8rem", padding: "1rem 0" }}
                        onClick={() => newAddressHandler()}
                      >
                        Add new address
                      </span>
                    </div>
                  </div>
                  <span>{addressMsg}</span>
                  <div className="line"></div>
                  <TextField
                    label="phone"
                    value={usersPhone}
                    type="tel"
                    required
                    className="phone-input"
                    onChange={(e) => setUsersPhone(e.target.value)}
                  />
                  <div className="two-inputs">
                    <TextField
                      label="street"
                      value={usersStreet}
                      required
                      className="input"
                      onChange={(e) => setUsersStreet(e.target.value)}
                    />
                    <TextField
                      label="house nr"
                      value={usersHouseNr}
                      required
                      className="input"
                      onChange={(e) => setUsersHouseNr(e.target.value)}
                    />
                  </div>
                  <div className="two-inputs">
                    <TextField
                      label="city"
                      value={usersCity}
                      required
                      className="input"
                      onChange={(e) => setUsersCity(e.target.value)}
                    />
                    <TextField
                      label="postal-code"
                      value={usersPostalCode}
                      required
                      className="input"
                      onChange={(e) => setUsersPostalCode(e.target.value)}
                      onKeyDown={(e) =>
                        e.key === "Enter" ? addressHandler() : ""
                      }
                    />
                  </div>

                  <button
                    className="button-white"
                    style={{ display: action === "add" ? "flex" : "none" }}
                    onClick={() => addAddress()}
                  >
                    Add
                  </button>

                  <button
                    className="button-white"
                    style={{ display: action === "change" ? "flex" : "none" }}
                    onClick={() => addressHandler()}
                  >
                    Save changes
                  </button>
                </div>
              )}
            </div>
          )}
        </LoggedInComponent>
      )}
    </>
  );
};

