import React, { useEffect, useState } from "react";
//styling
import styled from "styled-components";
//location
import { useLocation } from "react-router-dom";
//action
import { getItemsOnSale } from "../actions/itemsAction";
//redux
import { useDispatch, useSelector } from "react-redux";
//components
import Card from "../components/Card";
import SimpleClothesHeader from "../components/SimpleClothesHeader";
import SaleLinks from "../components/SaleLinks";
import ShowLoading from "../components/ShowLoading";

const SalePage = () => {
  const location = useLocation();
  const dispatch = useDispatch();
  const [sort, setSort] = useState("");
  const gender = location.pathname.split("/")[2];
  const category = location.pathname.split("/")[3];
  const { saleLimit, itemsOnSale, isLoading } = useSelector(
    (state) => state.item
  );
  //useEffects
  useEffect(() => {
    dispatch(getItemsOnSale(gender, category));
  }, [dispatch, gender, category, sort, saleLimit]);
  return (
    <>
      {itemsOnSale && (
        <ShowLoading isLoading={isLoading}>
          <SalePageComponent>
            <div className="left-side">
              <SaleLinks gender={gender} category={category} />
            </div>
            <div className="right-side">
              <div className="items-header">
                <SimpleClothesHeader
                  sort={sort}
                  setSort={setSort}
                  length={itemsOnSale.length}
                />
              </div>
              <div className="items-display">
                {itemsOnSale.map((item) => (
                  <Card key={item._id} id={item._id} item={item} />
                ))}
              </div>
            </div>
          </SalePageComponent>
        </ShowLoading>
      )}
    </>
  );
};

const SalePageComponent = styled.div`
  display: flex;
  margin-top: 2rem;
  .left-side {
    width: 20%;
    position: Fixed;
    display: flex;
    flex-direction: column;
    align-items: center;
  }
  .right-side {
    width: 80%;
    margin-left: 20%;
    .items-display {
      display: flex;
      flex-wrap: wrap;
      @media screen and (max-width: 1000px) {
        align-items: Center;
      }
    }
    @media screen and (max-width: 1000px) {
      width: 100%;
      margin: 0;
    }
  }
`;
export default SalePage;
